css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {
.col-left {
    background: none repeat scroll 0 0 #FFFFFF !important;
    border-right: 3px solid #C2CFF1 !important;
}
.dr-label:hover {
     background-color: #C2CFF1 !important;
}
.dr-nav-all .dr-label {
    background-image: url("data:image/png;base64,") !important;
}
.dr-nav-popular .dr-label {
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAACXBIWXMAAAsSAAALEgHS3X78AAAAMklEQVR42mP4//8/AzmYZA0Nx6L+g/Ag0AiTAOEhphEXn2iFQ1njqlWr/pODyUrgIAwAOZ8vBQfaMigAAAAASUVORK5CYII=") !important;
}
.dr-nav-diggs .dr-label {
    background-image: url("data:image/png;base64,") !important;
}
.dr-nav-saved .dr-label {
    background-image: url("data:image/png;base64,") !important;
}
.dr-persistent-nav .dr-label {
    background-position: 10px 4px !important;
     background-size: 14px 14px !important;
}
.dr-label {
    padding: 0.2em 0 0.2em 42px !important;
    color: #000000 !important;
    text-shadow: none !important;
}
.dr-nav-all, .dr-nav-popular, .dr-nav-diggs , .dr-nav-saved {
    border-bottom: 1px solid #C2CFF1 !important;
}
.dr-feed-selected > .dr-label, .dr-feed-selected > .dr-label:hover {
    background-color: #FFFFFF !important;
    border: medium none !important;
    font-weight: bold !important;
}
.dr-root-feed-list > .dr-feed-list-item .dr-feed-badge {
    left: 20px !important;
}
.dr-feed-badge {
    box-shadow: none !important;
    padding: 0 !important;
    top: 0.33em !important;
}
.dr-folder-toggle {
    background-image: url("data:image/png;base64,") !important;
    background-position: 1px 7px !important;
    background-size: 25px 25px !important;
    top: -5px !important;
}
.dr-feed-badge {
    left: 30px !important;
}
.dr-feed-list-folder .dr-feed-label {
    padding-left: 50px !important;
}
.dr-feed-list-folder[data-folder-expanded="false"] .dr-folder-toggle {
    background-image: url("data:image/png;base64,") !important;
    opacity: 0.4 !important;
}
.col-left {
    width: 236px !important;
}
.dr-label-txt {
    max-width: 170px !important;
}
.col-right {
    margin-left: 239px !important;
}
.feed-stream-header {
    background: none repeat scroll 0 0 #EBEFF9 !important;
    border-bottom: 1px solid #C2CFF1 !important;
    padding: 0.5em 0 !important;
}
.feeditem-list {
    border-bottom: 1px solid #CACECF !important;
    border-top: medium none !important;
}
.feeditem-list:hover {
    background-color: #F2F4FF !important;
}
.feeditem-list .feeditem-feedtitle {
    line-height: 27px !important;
    padding: 0 5px 0 0px !important;
    float: right !important;
    text-align: right !important;
}
.feeditem-list .feeditem-list-content {
    height: 27px !important;
    line-height: 27px !important;
    padding: 0 0 0 1px !important;
    direction: rtl !important;
    text-align: right !important;
}
.feeditem-list .feeditem-fuzzytime {
    margin-left: 7px !important;
    margin-right: 7px !important;
}
.story-actions {
    margin-left: 70px !important;
    left: 1em !important;
    right: none !important;
}
.feeditem-list .story-actions-container {
    width: 199px !important;
    left: 0 !important;
    right: auto !important;
    text-align: left !important;
}
.feeditem-list .story-data {
    margin-left: 190px !important;
    margin-right: 0 !important;
    padding-right: 10px !important;
}
.feeditem-list.expanded {
    border: 2px solid #6688EE !important;
    box-shadow: none !important;
}
.feed-item-focused .feeditem-feedtitle, article.expanded .feeditem-feedtitle, .feed-item-focused .feeditem-list-content, article.expanded .feeditem-list-content {
    background: none repeat scroll 0 0 #F2F4FF !important;
}
.no-flexbox .item-listview-combo .feeditem-list-content {
    margin-right: 165px !important;
    margin-left: 2px !important;
}
.feeditem-read {
    background: none repeat scroll 0 0 #F2F4FF !important;
}
.story-detail-content {
    padding-bottom: 20px !important;
    padding-top: 10px !important;
    width: 700px !important;
}
.feeditem-list .feeditem-feedtitle {
    width: 165px !important;
}
.story-detail-view {
    direction: rtl !important;
    text-align: right !important;
}
.story-meta {
    font-family: tahoma !important;
    font-size: 12px !important;
}
.detail-body {
    font-family: tahoma !important;
    font-size: 13px !important;
}
.story-detail-view-container {
    padding: 10px 0 !important;
}
.story-score {
    left: none !important;
    right: 0 !important;
}
.feeditem-headline {
    margin-right: 0 !important;
}
*[dir="rtl"] {
    font-family: tahoma !important;
    font-size: 13px !important;
    text-align: right;
    direction: rtl;
}

}
