css:
@-moz-document url-prefix("http://www.cracked.com"){

  body, header.headerWrapper div.mainHeader {
    background: #4f0101 url(http://i3.photobucket.com/albums/y60/joequincy/cracked_bg_zps250cd004.jpg~original) no-repeat center top !important;
  }

body>header.headerWrapper#miniPersistentNav {
  background-image: url(http://i3.photobucket.com/albums/y60/joequincy/cracked_nav_bg_zpsd09d3ef5.jpg~original) !important;
}
body>header.headerWrapper#miniPersistentNav div.mainHeader {
  background-image: url(http://i3.photobucket.com/albums/y60/joequincy/crackedHeader_zps618cb9d6.png~original) !important;
  background-position-x: 208px;
}

section.mainFrameModule article.module section p, section.mainFrameModule article.module section .subheading, section.mainFrameModule article.module section .Title, section.mainFrameModule article.module header h1, section.mainFrameModule article.module section p font {
  font-family: Georgia,Helvetica,Arial,sans-serif;
}
section.mainFrameModule article.module section p {
  font-size: 13px;
}
section.mainFrameModule article.dropShadowBottomCurvedModule {
  background-color: #FFF;
}

}
