css:
@-moz-document domain("justin.tv") {
body {
	background: #222 url(http://i.imgur.com/1FJvmx9.png) repeat center top !important;
  	color: #FFF;
}
.chat_text_inverse {
	background-color: #1A1A1A;	
}
.chat_column {
	background-color: #1A1A1A;	  
}
.channel-section {
	background-color: #1a1a1a !important;
}
.chat_speak .chat {
	background-color: #222;
	color: #FFF;
  	border: none !important
}
#chat_lines {
	background-color: #333;
	color: #FFF;
  	border-radius: 10px;
}
li.fromjtv {
	color: #FFF;
	text-shadow: 0 1px 0 #000;
	background: #222;
}
div.channel_status {
	background-color: #1a1a1a;
	border-color: #111;
  	color: #5d6b7b;
}
#views_tab {
	border-color: #111;
}
#player-settings {
	border-color: #111;
}
#fb_share_wrapper {
	background: #1a1a1a;	  
}
.channel-section {
	background: #1a1a1a;
  	color: #1F3042;
}
.player-options li {
	background-color: #111;
}
.player-options li:hover {
	background-color: #0f0f0f;
}
.categories img {
	background-color: transparent;
}
.categories .channel-subcategory-container {
	background-color: transparent;
}
.dropmenu_links_container {
	background: #1d2f41;
  	border-radius: 10px;
}
.chat_timestampdiv,
.chat_option {
	color: #FFF;
}
.dmactions {
	background-color: transparent;
}
.dmoptions {
	background-color: transparent;
}
li.line .nick {
	color: #5d6b7b !important;	
}
html {
	overflow: auto;
}
body {
	position: absolute;
	top: 0px;
	left: 0px;
	bottom: 0px;
	right: 0px;
	padding: 0px; 
	overflow-y: scroll;
	overflow-x: hidden;
}
::-webkit-scrollbar {
	width: 15px;
}
::-webkit-scrollbar-track {
	background-color: #111;
}
::-webkit-scrollbar-thumb {
	background: #1d2f41;
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}
::-webkit-scrollbar-thumb:window-inactive {
	background: #1d2f41; 
}
.channel_status span.status_title {
	display: block;
	color: #1d2f41;
	font-size: 13px;
	text-transform: uppercase;
}
.global-footer-title {
	color: #FFF;
}
.global-header-user-content {
	background: #1a1a1a;
}
.global-header-user-mgmt-link a {
	background: #000;
}
.global-header-explore-content {
	background: #1a1a1a;
}
.global-header-category {
	background: #000;
}
.line .mod {
	background: #245060;	
}
.frontpage-content {
	background: none;
  	padding: 0px;
}
.frontpage-categories-wrapper {
	background: #1a1a1a;
	border-radius: 10px;
}
.frontpage-category-wrapper {
	border-right: #111;
}
.frontpage-category-wrapper:hover {
	background: #0f0f0f;
}
.frontpage-category:hover {
	text-decoration: none;
}
.frontpage-title {
	color: #425060;
	text-shadow: 1px 1px 1px #111; 
}
.frontpage-blurb {
	background: none;
}
.frontpage-right-ad {
	height: 0px;
	width: 0px;
	margin: 0px;
	padding: 0px;
}
.frontpage-featured {
	width: 630px;
	background-color: #1a1a1a;
	padding: 10px;
}
.frontpage-related-block {
	margin: 5px 0 20px;
	padding: 20px 10px 10px 0px;
	text-align: center;
}
.frontpage-related {
	width: 280px;
	padding: 10px;
	background: #1a1a1a;
}
.frontpage-right-ad-hide {
	margin: 0px 0 20px;
}
.frontpage-channel:hover {
	background-color: #111;
}
.frontpage-banner h1 {
	color: #425060;
	text-shadow: 1px 1px 1px #000;
}
#iphone_banner_in {
	height: 0px;
}
.chat_line {
	color: #FFF/*[[chatcolors]]*/;
}
.global-header-category:hover {
	background: #111;
}
.directory-channel:hover {
	background: #111;
}
.directory-categories-wrapper {
	background-color: #111;
	border-radius: 5px;
	padding-left: 5px;
}
.directory-category-wrapper {
	border-right: 1px #090909 solid;
}
.directory-category-selected-wrapper + .directory-category-wrapper {
	border-left: 1px #090909 solid;
}
.directory-category-wrapper:hover {
	background-color: #090909;
}
.directory-wrapper {
	background-color: #1a1a1a;
	border-radius: 10px;
	padding: 15px;
	width: 1000px;
}
.pagelinks a, .pagelinks .prev_next span, .pages .pagelinks {
	background: #111;
	border-radius: 5px;
}
.not_linked {
	border-radius: 5px;
}
.directory-language-filter, .directory-subcategory-filter {
	background: #111;
	border-radius: 10px;
}
.directory-language-select, .directory-subcategory-select {
	background: #090909;
}
.directory-language-select ul, .directory-subcategory-select ul {
	background: #090909;
}
.directory-language-select a:hover, .directory-subcategory-select a:hover {
	background: #1d2f41;
	color: #FFF;
}
.settings-main {
	background: #1a1a1a;
	border: 1px solid #1a1a1a;
}
.dashboard-channel-default-thumb a {
	border: 1px solid #1d2f41;
	text-shadow: 1px 1px 0 #ffffff;
}
.settings-main-inner {
	border: 1px solid #1a1a1a;
}
.global-alert {
	background-color: #1a1a1a !important;
	box-shadow: inset 0px -5px 30px #000;
}
.settings-menu-item.selected > a:after {
	border-right-color: #1a1a1a;
}
.dashboard-channel:hover {
	background-color: #111;
}
.settings .box {
	border-bottom-color: #111;
	box-shadow: inset 0 -1px 0 #1a1a1a;
}
.message-list {
	border-bottom-color: #111;
	box-shadow: inset 0 -1px 0 #1a1a1a;
}
.message {
	color: #FFF;
}
.settings .input-text, .settings .input-textarea {
	background-color: #111;
	border: 1px solid #0a0a0a !important;
	color: #FFF;
}
.settings .row {
	color: #FFF;
}
.settings {
	color: #FFF;
}
p.note {
	color: #1a1a1a;
}
.settings .input-select-overlay {
	background-color: #090909 !important;
	color: #1d2f41;
	border: 0px;
	border-raidus: 10px;
}
.input-select {
	background-color: #090909 !important;
	color: #1d2f41;
	border: 0px;
}
.global-header-wrapper {
	margin: 0px auto;
}
.chat-column-title {
	background: #1a1a1a;
}
.chat-column-head i {
	border-right-color: #111; 
}
#jtv_chat {
	background: #333;
}
#chat_speak {
	background: #1a1a1a;
}
#chat_text_input_wrapper {
	background: #111;
	border: 1px #111 solid;
}
#chat_text_input {
	color: #FFF;
}
.chat-ad-wrapper {
	padding: 0px;
	display: none;
}
.related-videos {
	background: #333;
}
.user-link {
	color: #0066CC;
}
.channel-details {
	background: #1a1a1a;
}
.channel-category {
	background: #1a1a1a;
}
.feedback-link {
	color: #FFF;
}
.channel-player-info {
	background: #1a1a1a;
	border-bottom: 1px #000 solid;
}
.channel-player-followers {
	border-left: 1px #000 solid;
}
.channel-settings {
	border-left: 1px #000 solid;
}
.channel-settings-icon:hover {
	background-color: #111;
}
.channel-settings-options {
	background: #111;
	border: 1px #000 solid;
}
.channel-settings-options li:hover {
	background: #0a0a0a;
}
.channel-player-views {
	color: #FFF;
}
#chat-viewers {
	background-color: #111;
}
body.custom #broadcast_callout {
	background: #111 !important;
}
#broadcast_message {
	background: #1a1a1a;
}
.mask150x113 .screencap {
	border-radius: 5px;
	width: 120px;
	height: 108px;
	margin-top: 0px;
	margin-left: 0px;
}
.mask_image {
	content: url('http://i.imgur.com/iYNXQ3N.png');
	background: transparent;
}
.overlay {
	border-bottom-right-radius: 5px;
	border-bottom-left-radius: 5px;
}
#wrapper, #content {
	background: #1a1a1a;
	padding: 15px;
	border-radius: 10px;
	color: #FFF !important;
}
#content {
	padding-top: 0px;
}
.jobs-ul_header, .position_desc, .jobs-li span, a[name] {
	color: #FFF !important;    
}
.choice, #enterprise, .features {
	background: #2a2a2a !important;
	border-bottom: solid 4px #111 !important;
}
.bottom-leaderboard-adslot, .iphone_banner, .directory-house-ad, .directory-left-ad, .frontpage-right-ad, .frontpage-right-ad-hide, .frontpage-banner {
 	display: none !important;
}
.directory-wrapper {
	margin-top: 30px;        
}
}
