css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.salon.com"), domain("salon.com") {

.grid_4, #footer, .contentFootContainer,.related-video,
 div.horizontalPostList, .nextArticle, .related_stories {
    display: none !important;
    }
.container_12 .grid_8, .headContainer {
    width: 960px !important;
    }
.masthead {
    height: 60px  !important;
    }
.headContainer {
    height: 50px  !important;
    }
.articleContent, .fyre .fyre-comment-stream  {
    width: 700px !important;
    }
.article-info {
    width: 610px !important;
}
nav, .recentColumn .story .topics {
    float: right !important;
    }
hgroup .logo {
    float: left !important;
    }
h1.logo a {
    background: #000 url("data:image/png;base64,") no-repeat  0 0 !important;
    height: 50px !important;
    }
h2, .articleInner p {
    font-size: 2.0em  !important;
    line-height: 1.2 !important;
    color: #676767 !important;
    }
article.story h2 a, .articleInner h1 a {
    color: #051498 !important;
    }
.recentColumn article.story p {
    font-size: 1.5em !important;
    line-height: 1.3 !important;
    color: #676767 !important;
    }
p.topics, .fyre .fyre-comment p  {
    font-size: 12px !important;
    }
.fyre .fyre-comment p  {
    font-size: 20px !important;
    }


}
