css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.huffingtonpost.com"), domain("huffingtonpost.com") {

#updated_and_search > div:nth-child(2) > form:nth-child(1),
div.float_right:nth-child(5), .footer_search, #footer,
#blogroll_wrapper, #hp_bignews_pages_container,
 .image_overlay_icon, .mod-follow, .share_boxes_new,
 .bnpage_vertical_fb_like, #updated_and_search > div:nth-child(2),
 #threeup_top_wrapper, .news_main_info .fb_like_cont, #chicklets,
 .print-link, #submissions_shortcuts, .sbm-main-new, #partner_box,
 .link_entries, #around_the_web, #taboola-autosized-1r-sc,#taboola-autosized-1r-sc,
 #taboola-autosized-1r-organic, #taboola-autosized-generated-2r, .rail, footer, #huffpost-web-fb-comments,
    .group.row.right, .modern-theme .component-container.footer, .bpage-theme .component-container.footer,
    .follow-us-below-entry-component, .modulous-placement-component__header, .comments-component__header {
    display: none !important;
    }

div#wrapper, #updated_and_search .left, #ap_wire_scroll_main, #ap-wires-scroll-container  {
    width: 1000px !important;
    }
    div#masthead {
        margin-left: -10px !important;
    }
.bpage_body .top_nav_menu, .bpage_body #top_nav, .bpage_body #fake_top_nav {
    margin-left: 0 !important;
}

#updated_and_search .left, #ap_wire_scroll_main, #ap-wires-scroll-container, 
.top_nav_menu, #top_nav, #fake_top_nav, .grid.full   {
    width: 1000px !important;
    }
    .topnav_first_row a {
        font-size: 10px !important;
    }
    .topnav_first_row > ul > li > a, .topnav_first_row > ul > li > div > a {
        padding: 0 !important;
    }
div#wrapper_inner, div#masthead, #top_featured_news {
    width: 980px !important;
    }
.grid_block.right {
    width: 665px !important;
    }

.grid_block.left, #center_entries, #right_col,
.grid_v_fp_half_bl {
    width: 300px !important;
    }
.grid_block.left, #the_blogs, .entry .subhead, .entry h4,
.most_popular_entry_wrapper a, #all_frontpage_widgets,
#all_frontpage_widgets h3.most_popular, div#right_column_entries  {
    width: 280px !important;
    }
div.grid.two_thirds {
    width: 860px !important;
    }
.standard_content_page_layout .component-container.content, 
.main .column.entry {
        width: 800px !important;
    }
#news_content.news_content_regular div#news_entries div.entry div.entry_right.full, #blog_content #conversations-huffpost-web .conversations-offset,
.comments, .news_content_regular #conversations-huffpost-web .conversations-offset, #news_entries,
div#blog_entries div.entry div.entry_right {
    width: 610px !important;
    padding-left: 30px !important;
    }
.entry h4, .entry .image_wrapper a img {
    max-width: 100% !important;
    }
.top_nav_menu .bignews_items li, .new-topnav_second_row .featured li {
    padding: 0px 22px 0px 20px !important;
    }
div#news_entries div.entry {
    margin-bottom: 0 !important;
    }
#the_blogs h3 {
    font-size: .80em !important;
}
#the_blogs .entry p, .hp-conversations .body-text,
div#all_featured_posts div.entry p,
div#top_featured_post div.entry p {
    font-size: 1.5em !important;
    line-height: 1.2 !important;
    }
.entry-component__content, .entry .content > p, .entry .content > blockquote, .entry .content > blockquote p {
    color: #484848 !important;
        font-size: 26px !important;
    line-height: 1.4 !important;
    }
#center_entries h4, #right_col h4, #center_entries h4 a, #right_col h4 a, h4 a {
    font-size: 1.3em !important;
    line-height: 1.2 !important;
    }
.most_popular_entry_title, div.blog_content p,
.news_content_regular div#news_entries div.entry_content p, div#news_entries .entry-wide div.entry_content p,
.comment_body, h3 a, div#news_entries div.entry_content p, .entry_content span   {
    font-size: 1.8em !important;
    line-height: 1.2 !important;
    }
.jd_menu ul li a, .topnav_first_row .menu a {
    font-size: 16px !important;
    line-height: 1.3 !important;
}
div.blog_content p, #the_blogs .entry p, .hp-conversations .body-text,
.comment_body, .entry_body_text p, div#news_entries div.entry_content p, .entry_content span    {
     color: #6a6a6a !important;
     }
    
a:hover {
    text-decoration: none !important;
    border-bottom: 3px dotted red !important;
}


.image_wrapper a:hover, .splash a:link:hover, #splash_block a:hover, .top_nav_menu, #top_nav a:hover, #headline_image_container a:hover,
.author-component .social-component__link:hover, .floating-share-component__action:hover,
.main-nav-component__category-link:hover, .masthead-component__branding a:hover, .blog-banner .title:hover,
nav.main .toplevel a:hover, nav.main .stories a:hover {
    border-bottom: none !important;
}
.hp-conversations .comment-item.default > .comment-body, 
.hp-conversations .overlay-item.default > .comment-body, 
.hp-conversations .single-thread-item.default > .comment-body,
.grid_block.right, .comment_top, .comment_item {
    background: none !important;
    }
.superuser_comment {
    background-color: rgb(241, 229, 245) !important;
    }
.networker_comment {
    background-color: rgb(252, 235, 223) !important;
    }
.search-huffpost {
    float: right !important;
    }
.ap-wires-container {
    margin-left: 370px !important;
    }
div#right_col {
    border-left: 1px solid rgb(204, 204, 204) !important;
    padding-left: 10px !important;
    }

}
