css:
@-moz-document domain(inoreader.com) {

#parent_evernote{display:none;}
#parent_readability{display:none;}
#parent_instapaper{display:none;}
#parent_pocket{display:none;}
#parent_trending{display:none;}
#parent_catalog{display:none;}

.ar:not(.article_unreaded) > .article_header{opacity:0.75}
.ar.article_unreaded > .article_header{box-shadow: inset 0px 0px 3px 0px black;}

.ar[data-suid$="0"]  > .article_header{background-color: hsla(  0,100%,90%,1);}
.ar[data-suid$="1"]  > .article_header{background-color: hsla( 30,100%,88%,1);}
.ar[data-suid$="2"]  > .article_header{background-color: hsla( 45,100%,85%,1);}
.ar[data-suid$="3"]  > .article_header{background-color: hsla( 60,100%,82%,1);}
.ar[data-suid$="4"]  > .article_header{background-color: hsla( 75,100%,83%,1);}
.ar[data-suid$="5"]  > .article_header{background-color: hsla( 90,100%,83%,1);}
.ar[data-suid$="6"]  > .article_header{background-color: hsla(105,100%,85%,1);}
.ar[data-suid$="7"]  > .article_header{background-color: hsla(120,100%,88%,1);}
.ar[data-suid$="8"]  > .article_header{background-color: hsla(220,100%,88%,1);}
.ar[data-suid$="9"]  > .article_header{background-color: hsla(280,100%,88%,1);}
.ar[data-suid]:hover > .article_header{background-color: white;}

#subscriptions_nav > div:nth-child(1) > a:nth-child(1) > img:nth-child(1){display:none;}

.parent_div.parent_div_inner.selected_parent{box-shadow: inset 0 0 20px 0px red;}
.parent_div.parent_div_inner.selected_parent:hover{box-shadow: inset 0 0 20px 0px lime;}

.parent_div_inner_selected{box-shadow: inset 0 0 20px 0px red;}
.parent_div_inner_selected:hover{box-shadow: inset 0 0 20px 0px lime;}

.parent_div_inner:hover{box-shadow: inset 0 0 20px 0px lime;}

.article_title_link.bluelink:link, .article_content a:link{
	-moz-transition: background-color 1s ease-in 0s;
	-webkit-transition: background-color 1s ease-in 0s;
	-o-transition: background-color 1s ease-in 0s;
}
.article_title_link.bluelink:hover, .article_content a:hover{
	background-color: lime;
	-moz-transition: background-color 1s ease-out 0s;
	-webkit-transition: background-color 1s ease-out 0s;
	-o-transition: background-color 1s ease-out 0s;
}
.article_title_link.bluelink:active, .article_content a:active{
	background-color: lime;
	-moz-transition: none;
	-webkit-transition: none;
	-o-transition: none;
}
}
