css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("syosetu.com") {
  body {
    background-color: #E0E0E0 !important;
  }
  
  a {
    color: #2200C1 !important;
  }
  a:visited {
    color: #551A8B !important;
  }
  #novel_color {
    color: #000000 !important;
    font-family: "メイリオ", Meiryo, "Hiragino Kaku Gothic Pro", "ヒラギノ角ゴ Pro W3", "ＭＳ Ｐゴシック", "Lucida Grande", "Lucida Sans Unicode", Arial, Verdana, sans-serif;
    font-size: 13pt;    
  }
  .novel_view {
    font-size: 100% !important;
    line-height: 1.4em !important;
    width: 100% !important;
  }
  #novel_contents {
    max-width: 730px;
    margin: 0 auto !important;
  }
  #user_info,
  .kokuti_box,
  #direct,
  #important_message {
    display: none !important;
  }
  hr,
  #navigate,
  #footer,
  #logout {
    display: none !important;
  }
  ul.writter_menu {
    margin: 0 !important;
    border: none;
    margin-bottom: 5px !important;
  }
  .top_right {
    height: auto;
    width: auto;
    float: none;
  }
  .koukoku_index_header {
    margin: 5px;
    text-align: center;
  }
  #writter_menu_top {
    background-color: #ffffff;
    margin-bottom: 5px;
  }
  #writter_menu_top li {
    margin: 0px !important;
  }
  #writter_menu_top li.menu_h {
    padding: 7px 4px 6px 6px !important;
  }
  #favuserblog,
  #sub_top {
    margin-top: 0px !important;
  }
  #extra {
    padding-top: 0px !important;
  }
  #header {
    margin-bottom: 10px !important;
    background-color: #fdfdfd;
  }
  #title_name a:after,
  #narou_link:after {
    content: "小説家になろう";
    font-size: 130% !important;
    font-weight: bold !important;
    padding-left: 8px !important;
  }
  #title_name a img,
  #narou_link img{
    display: none !important;
  }
  #container {
    padding: 0px !important;
  }
  #container,
  #fav {
    background-color: inherit !important;
  }
  #favnovel,
  #favuser {
    margin: 0px 10px 10px 0 !important;
  }
  #favuserblog,
  #favusernovel {
    border: none !important;
    margin-bottom: 8px !important;
  }
  .xuser_top,
  #home {
    border: none !important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
  }
  .xuser_top a {
    border: none;
  }
  #pd_box {
    margin: 0px 0px 5px 0 !important;
  }
  #mypagelink {
    border: none !important;
    padding: 0px;
    background-color: inherit !important;
    margin: 0px 0px 5px 0 !important;
  }
  #mypagelink li {
    margin: 0px 0px 2px 0px !important;
    border: none !important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
  }
  #fav {
    border: none !important;
    padding: 0;
    margin: 0;
  }
  .sub_searchbox {
    margin: 0 0 5px 0 !important;
    padding: 5px !important;
  }
  .sub_searchbox input[type="text"] {
    width: 90% !important;
  }
  .search_box,
  .news{
    margin: 0 0 5px 0 !important;
    padding: 5px !important;
  }
  .waku,
  .news,
  .sub_searchbox,
  .review,
  .search_box,
  #main_ninki_keyword {
    border: none !important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
    background-color: #fafafa;
  }
  .rankmain_box,
  .xuser_box,
  #menu_box,
  #favnovel,
  #favuserblog,
  #favusernovel,
  #favuser,
  .normal_box {
    padding: 0px 0px 5px 0px !important;
    border: none !important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
    background-color: #fafafa;
    margin: 0px 0px 5px 0px !important;
  }
  .xuser_top,
  .xuser_box,
  .normal_box{
    margin: 0px 0px 5px 0 !important;
  }
  .ranking_inbox {
    padding: 0 10px;
  }
  #menu_box ul {
    border: none !important;
  }
  .favnovel_list .favnovel_title {
    font-weight: normal !important;
  }
  #favuserblog a,
  a.tl {
    color: #0033CC !important;
  }
  #favuserblog a:visited,
  a.tl:visited {
    color: #934C7B !important;
  }
  ul.favnovel_list .title,
  .favusernovel_title a,
  #mypagelink a,
  h3,
  .favusernovel_type a,
  #x_top a,
  .ex{
    color: #000000 !important;
  }
  #sub1 {
    float: none !important;
    width: auto !important;
    margin-top: 0px !important;
    padding-top: 0px !important;
  }
  #sub1 br,
  #top_kensaku > div  {
    display: none !important;
  }
  .bigkoukoku {
    margin: 0px !important;
  }
  #contents {
    background-color: inherit !important;
  }
  .contents1 {
    background: none !important;
    width: 730px !important;
  }
  .contents1 .margin_r20 {
    font-size: 140% !important;
    font-weight: bold !important;
  }
  dl.novel_sublist2 dd {
    width: 70% !important;
}
  dl.novel_sublist2 dt {
    width: 30% !important;
    padding: 10px 0 0 !important;
  }
  dl.novel_sublist2 dd {
    padding: 10px 0 0 !important;
  }
  .xuser_top a:hover {
    border: none !important;
  }
}
@-moz-document url-prefix(https://syosetu.com/user/),
 url-prefix(https://syosetu.com/xuser/top/),
 url-prefix(http://syosetu.com/user/),
 url-prefix(http://syosetu.com/xuser/top/)  {
  
  #contents {
    margin: 0px 0px 0 0 !important;
    width: 750px !important;
  }
  #main_top {
    width: 495px !important;
    margin: 0 5px 0 10px !important;
  }
  #sub_top {
    width: 240px !important;
  }
}
