css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plus.im") {
#index_main_pane {background-color:#404040 !important;}
.index_main_logo_text {color:#FFFFFF !important;}
.chat_msg {background-color:#404040; color:#DDDDDD;}
.chat_wr {background-color:#404040;}
.jspPane {background-color:#505050}
.chat_nick {background-color:#404040 !important; font-weight: bold !important;}
.chat_time, .footer, .msgarea.text.ui-widget-content.ui-corner-all, .ui-widget-content, .chat_window_infobar, .msgareafooter, .jspContainer, .cle, .cl_footer, .cl_footer_right, .header, .status-select, #status_psm {background-color:#404040 !important; color:#EEEEEE !important}
.cle_psm {color:#BBBBBB !important;}
.chat_msg_common_header.IN {color:rgb(69, 170, 251) !important;}
.chat_msg_common_header.OUT {color:rgb(113, 206, 104) !important;}
a:link {color: rgb(228, 139, 56) !important;}
a:visited {color: rgb(228, 139, 56) !important;}
div.chat_msg_common_header.OUT span.chat_me_msg {background:none !important; color:rgb(113, 206, 104) !important; font-weight: bold !important;}
div.chat_msg_common_header.IN span.chat_me_msg {background:none !important; color: rgb(69, 170, 251) !important; font-weight: bold !important;}
div.chat_msg_common_header {background-image:none !important;}
div.chat.selected {background-color: #505050 !important; border-bottom: none !important;}
div.chat {background-color: #404040 !important; border-bottom: none !important;}
div.chat.newmsg {background-color: rgb(122, 58, 15) !important; border-bottom: none !important;}
.cl_block_head {color: #DDDDDD !important; background-color: #505050 !important}
.cl_block_head span {color: #DDDDDD !important; background-color: #505050 !important}
div.header table {background-color: #404040 !important;}
div.footer div {border-top: 2px solid #505050 !important; border-bottom: 0px !important;}
textarea.msgarea {border: 2px solid #505050 !important;}
.jspVerticalBar .jspTrack {background: #404040 !important; border-left: 1px solid #505050 !important; border-right: 1px solid #505050 !important;}
.jspVerticalBar .jspDrag {background: #505050 !important; border: 1px solid #404040 !important;}
.cl_footer_right {background: #505050 !important;}
.chat_history_bar {color: #DDDDDD !important; background: #505050 url(https://plus.im/static/img/clock.png) no-repeat 10px center !important; border: 1px solid #505050 !important;}
div.chatlog a.chat_history_link {color: rgb(228, 139, 56) !important;}
div.chatlog a.chat_clear_link {color: rgb(236, 47, 0) !important;}
.ui-dialog .ui-dialog-titlebar {background: #505050 !important; border-color: #404040 !important;}
.ui-dialog .ui-dialog-title {color: #DDDDDD !important; text-shadow: 0 1px 0 rgba(80, 80, 80, 0.8) !important;}
div.chatwin.selected {border: 1px solid #404040 !important;}
.chat_avatars .chats_close_btn, .group_chat_avatars .chats_close_btn {margin-top: 10px !important; padding: 7px 18px 11px 3px !important; width: 0px !important; height: 0px !important; background: url('https://plus.im/static/img/close.png') no-repeat !important;}
a.ui-state-default span.ui-button-text {border-color: #505050 !important; background: #404040 !important; color: #DDD !important;}
a.ui-button span.ui-button-text {text-shadow: 0 1px 0 rgba(80, 80, 80, 0.8) !important;}
a.ui-state-hover span.ui-button-text {background: #505050 !important; border-color: #505050 !important; -webkit-box-shadow: none !important; box-shadow: none !important;}
a.ui-button span {border: 2px solid !important;}
a.ui-button-text-only span.ui-button-text {padding: 6px 12px !important;}
#status_psm {border: 2px solid #505050 !important;}
#contacts_search {border: 2px solid #505050 !important; margin-bottom: 3px !important;}
#status_st {border: 1px solid #505050 !important; background: #404040 !important;}
#status_st option {background: #404040 !important;}
.pane-north {background: #404040 !important; border-bottom: 2px solid #505050 !important;}
.pane-north {background: -webkit-linear-gradient(top, #303030, #404040) !important;}
.pane-north {background: -moz-linear-gradient(top, #303030, #404040) !important;}
.pane-north {background: -ms-linear-gradient(top, #303030, #404040) !important;}
.pane-north {background: -o-linear-gradient(top, #303030, #404040) !important;}
body.loaded_metal, body.loaded_default {background: #303030 !important;}
div.chatwin {background-color: #303030 !important;}
.pane-south {color: #DDD !important;}
.pane-south a {color: rgb(236, 47, 0) !important;}
div.chatlog a {color: rgb(228, 139, 56) !important;}
.ui-menu .ui-menu-item a.ui-state-hover#ui-active-menuitem, .ui-menu .ui-menu-item a.ui-state-active {background-color: #505050 !important; border: 1px solid #505050 !important; color: #DDD !important;}
.ui-corner-all {color: #DDD !important;}
div.tabsel.selected {border: 1px solid #505050 !important; background-color: #505050 !important;}
div.tabsel {color: #DDD !important; background-color: #404040 !important; border: 1px solid #505050 !important; border-bottom-width: 0px !important;}
.header .ui-state-hover#tabsel_cl {background-color: #505050 !important;}
.header .ui-state-hover#tabsel_conv {background-color: #505050 !important;}
.ui-layout-west .header {border-bottom: 1px solid #505050 !important;}
#news-plate {background: #505050 !important; color: #DDD !important; line-height: 30px !important;}
#acc_manager_wrapper {background: #505050 !important;}
#top_buttons {background: #505050 !important;}
#top_buttons img {filter: invert(1) !important; -webkit-filter: invert(1) !important;}
.acc_img {-webkit-filter: drop-shadow(0px 0px 1px rgba(30,30,30,1)) brightness(0.8) !important; filter: drop-shadow(0px 0px 1px rgba(30,30,30,1)) brightness(0.8) !important;}
.ms-creative-position-header {display:none;}
.ms-creative-position-body {display: none;}
}
