css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
* {
font-size:small;
line-height:1.3em;
min-width:1px;
}

strong {
width:50% !important;
display:block !important;
}

#navigation ul {
width:200px !important;
background:#fff;
position:fixed !important;
top:0;
left:0;
margin:0 !important;
padding:0 !important;
}

#profile-image {
width:32px !important;
margin:0 !important;
padding:0 !important;
}

textarea {
width:100%;
height: 4em !important;
} 

fieldset {
width:200px !important;
background:#fff;
position:fixed !important;
top:0;
left:0;
}

tr {
width:100% !important;
margin:0 !important;
padding:0 !important;
}

td {
margin:0 !important;
padding:0 !important;
}

body,a,#navigation,#update-submit,#container,.info {
width:100% !important;
min-width:1px;
margin:0 !important;
padding:0 !important;
}

#content {
width:200px !important;
min-width:1px;
margin:0 !important;
padding:0 !important;
}

#header,#side_base,#footer,#user_search_form,label,.tabMenu,.yellow-box,.entry-meta {
display:none !important;
}

.thumb {
width:37px !important;
}

.wrapper {
width:100% !important;
min-width:1px;
margin:70px 0 0 !important;
padding:0 !important;
}
}
