css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("feedly.com") {
BODY.home {

    color: #F2F4FF !important; background-color:#A7CB8B  !important;
    font:20px  B nazanin,Georgia  !important;
}
.narrow #mainBar {
    margin-left: 236px !important;
}
#feedlyTabsHolder {
    width: 236px !important;
	direction:rtl;
	font-family:tahoma !important;

}
.narrow .panels {
    visibility: visible !important;
}
.narrow #feedlyTabs {
    opacity: 1 !important;
}
#feedlyTabs {
    padding-left: 0 !important;
    padding-right: 0 !important;
    width: 233px !important;
}
.navSelector {
    margin-left: 7px !important;
    width: 100px !important;
    padding: 0 !important;
}
.navSelector span {
    background-color: #E3E3E3 !important;
    background-image: -moz-linear-gradient(center top , #F9F9F9, #E3E3E3) !important;
    border: 1px solid #BBBBBB !important;
    color: #333333 !important;
    display: block !important;
    padding: 0 8px !important;
    text-transform: capitalize !important;
    text-align: center !important;
}
.pageActionBar img {
    background-color: #E3E3E3 !important;
    background-image: -moz-linear-gradient(center top , #F9F9F9, #E3E3E3) !important;
    border: 1px solid #BBBBBB !important;
    color: #333333 !important;
    padding: 0 8px !important;
    text-transform: capitalize !important;
    text-align: center !important;
}
.navSelector.selected {
    background-color: #E3E3E3 !important;
    background-image: -moz-linear-gradient(center top , #F9F9F9, #E3E3E3) !important;
    border: 1px solid #BBBBBB !important;
    color: #333333 !important;
    display: block !important;
    padding: 0!important;
    text-transform: capitalize !important;
    width: 95px !important;
}
.narrow #feedlyTabsHolder {
    background-image: none !important;
}
#feedlyTabsHolder.picturePicture, .dark #feedlyTabsHolder.picturePicture, .narrow #feedlyTabsHolder.picturePicture, .narrow .dark #feedlyTabsHolder.picturePicture {
    background-image: none !important;
    box-shadow: none;
    width: 236px !important;
}
#feedlyTabs div {
    margin: 0 !important;
}
#feedlyTabs div.tab:hover {
    background-color: #C2CFF1 !important;
}
#feedlyTabs div.tab:hover .list {
    background-color: #FFFFFF !important;
}
.tab .feedIndex:hover {
    background-color: #C2CFF1 !important;
}
#mytab, #savedtab, #organizetab {
    border-top: 1px solid #C2CFF1 !important;
    line-height: 24px !important;
}
#organizetab {
    border-bottom: 1px solid #C2CFF1 !important;
}
#navSelectorHolder {
    margin-top: 10px !important;
}
#myPanel {
    border-right: 3px solid #C2CFF1 !important;
}
#feedlyTabs .label {
    color: #000000 !important;
    font-size: 13px !important;
    line-height: 25px !important;
}
#feedlyPart0.area {
    padding: 0 0 102px 0px  !important;
}
#feedlyPart {
    padding-right: 0 !important;
	color: navy !important;
font-size: 65% !important; 
font-family: tahoma !important;
}
#feedlyPageHeader {
    background: none repeat scroll 0 0 #C2CFF1 !important;
    margin-right: 0 !important;
    margin-top: 0px !important;
}
.u0Entry {
direction:rtl !important;
	height:32px;
	background-color:#A7CB8B  !important;
}
.u0Entry .title {
font:21px  B nazanin,Georgia  !important;
color:black !important;
    line-height: 30px;
}
.u0Entry:hover {
    background-color: #EBEFF9 !important;
}
#floatingBar {
    background-color: #C2CFF1 !important;
}
.pageAction {
    opacity: 1 !important;
}
h1 .hhint {
    color: #000000 !important;
    line-height: 26px !important;
}
.action {
    color: #032AFF !important;
}
.hhint {
    background: none repeat scroll 0 0 #EBEFF9 !important;
    height: 30px !important;
    margin-top: 8px !important;
    padding-left: 10px !important;
}
#floatingBar .hhint {
    background: none !important;
}
.pageActionBar {
    margin-right: 20px !important;
}
.u0Entry {
    background-color: #A7CB8B  !important;
    border-top: 1px solid #CACECF !important;
    margin-left: 0 !important;
    padding-left: 5px !important;
}
.u0Entry .sourceInfo {
    float: left !important;
}
.condensed .inlineFrame {
    margin-bottom: 0 !important;
    margin-left: 0 !important;
    margin-top: 0 !important;
}
.inlineFrame {
    border: 2px solid #6688EE !important;
    padding: 0 24px 0 0 !important;
	background-color: #F3F3F3 !important;
}
*[dir="rtl"] {
    font-family: tahoma !important;
    font-size: 13px !important;
}
.condensed .entryholder .u100entry {
    max-width: 100% !important;
    padding-bottom: 50px !important;
	background-color: #F3F3F3;
}
.metadata {
    background: none repeat scroll 0 0 #F2F4FF !important;
    color: #515151 !important;
    padding: 7px !important;
}

.topWikiWidget {
    background: none repeat scroll 0 0 #F3F5FC !important;
    border-bottom: none !important;
    border-top: 1px solid #DEE7FF !important;
    bottom: 0 !important;
    left: 0 !important;
    margin-top: 0 !important;
    padding: 5px !important;
    position: absolute !important;
    width: 100% !important;
}
.highlightableIcon {
    opacity: 0.5 !important;
}
.inlineFrame .u100entry .title {
padding:2% 5% 0 5%!important;
max-width:none!important;
margin:0!important;
direction:rtl !important;
text-align:right !important;
	font:24px  B nazanin,Georgia  !important;
    font-weight: bold !important;
	color:navy !important;
}
.entryBody a {
    color: #2002AB !important;

}
.entryBody div{
	direction:rtl !important;
	margin-left: 20px !important;
	margin-right: -80px !important;
	font:21px  B nazanin,Georgia  !important;
	text-align:justify!important;
}
.entryBody p{
	direction:rtl !important;
	margin-left: 20px !important;
	margin-right: -80px !important;
	font:21px  B nazanin,Georgia  !important;
	text-align:justify!important;
}
.bottomWikiWidget {
    display: none !important;
}
.topWikiWidget {
    border-bottom: medium none !important;
    margin-bottom: 0 !important;
    padding-bottom: 0 !important;
}
.u0Summary div,span {
font-family:tahoma !important;
color:navy !important;
}
.tab .feedIndex {
  background-color:  #A7C !important;
  text-align:right;
  }
.u0Entry:hover .condensedTools {
    display: none !important;
}
#feedlyFrame {
text-align:right !important;
}
.u0Entry .title.read {
display:block !important;
background-color:#EBEFF9 !important;
}
#feedlyTabs .label {
text-align:right !important;
font-family:Tahoma !important;
}
#feedlyTabsHolder .icon {
float:right;
font-family:Tahoma!important;
}
.header target{
font-family:Tahoma!important;
}
.cell h2 {
font-family:Tahoma!important;
}
.cell div:hover {
background-color: #EBEFF9 !important;
}
