css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {

.new_post_label .new_post_label_icon {
background-image: url('http://media.tumblr.com/89d69e6379089420109cf9cda4439324/tumblr_inline_mpfesgoFu21qz4rgp.png') !important;
margin-top: 20px !important;
height: 94px !important;
width: 80px !important;
opacity: .9 !important;
background-size: 520px 190px !important;
}

#posts .post.new_post {
background-image: url('http://media.tumblr.com/519d680cb89d351ee4de5faff3c8b458/tumblr_inline_mpfertylKu1qz4rgp.jpg'), url('http://media.tumblr.com/9b2d3f7403418c782f3c46e6d230f15d/tumblr_inline_mpff3wBDp21qz4rgp.jpg') !important;
background-position: top center !important;
background-repeat: no-repeat, repeat !important;
}

#posts .post.new_post .arrow {
background: transparent url('http://media.tumblr.com/bc354d3a228478cd46589eb42f9d8223/tumblr_inline_mpfesahQdU1qz4rgp.png') !important;
background-repeat: no-repeat !important;
}

#new_post .cog {
margin-right: -15px !important;
}

div.l-content #new_post .new_post_label {
color: rgba(226,224,209,0) !important;
line-height: 3.9em;
font-weight: bold !important;
font-size: .85em !important;
}

#new_post .main_content {
background: rgba(255,255,255,0.95) !important;
color: rgba(0,0,0,0.7) !important;
border: none !important;
}

#new_post .popover_inner.clearfix, #new_post .post_options .popover_inner, #tag_suggest.popover, .post_options.popover {
background: rgba(255,255,255,0.95) !important;
}

}
