css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

domain("02ch.net"),
domain("iichan.hk"),
domain("2--ch.ru"),
domain("wakachan.org"),
domain("inach.org"),
domain("2chru.net")

{

/* GLOBAL */
html, body {
    background-color: #FFFFFF !important;
    color: #333333 !important;
    font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
    background:url("http://02ch.net/wi/src/1372911353287.jpg") bottom right no-repeat fixed #ffffff!important;
}
body{
    font:100%/1.4 Helvetica,Arial,sans-serif!important;
    
}
a {
    color:#D8323C!important;
    font-weight:700!important;
}
a:hover {
    color:#D8323C!important;
    font-weight:700!important;
}
/*
a:visited{
    font-weight:200!important;
    font-style:italic!important;
    opacity:0.75!important;
}*/
hr {
    border-style: solid none none none !important;
    border-width: 1px !important;
    border-color:#000000!important;
    width:100%!important;
    clear:both!important;
}
table, table td {
    border-style: none !important;
}

/* HEADER */
.adminbar {
    clear:both !important;
    float:right !important;
    font-size: .8em !important;
    text-align:right !important;
}
.adminbar a {
    font-weight: bold !important;
}
.logo{
    text-align:right!important;
    width:auto!important;
    margin:0 30px 0 0!important;
    color:#000000!important;
    font:200 100%/1.4 Helvetica,Arial,sans-serif!important;
    font-weight:700!important;
    font-size:150%!important;
}
.logo2 {
    text-align:center !important;
}
.theader, .passvalid {
    background-color:rgba(81,180,115,0.85)!important;
    text-align:center !important;
    padding:2px !important;
    color:#D8323C !important;
    clear: both !important;
    font-weight: bold !important;
    margin-bottom: .5em !important;
    border: solid 1px #FFFFFF !important;
    -moz-border-radius: 5px !important;
}

/* POSTFORM */
.postarea>form>table>tbody>tr{
    opacity:0.5!important;
}
.postarea>form>table>tbody>tr:hover{
    opacity:1.00!important;
}
.post {
    margin: 4px 0 !important;
    min-width: 35em !important;
    overflow: visible !important;
    word-wrap: break-word !important;
}
.postblock {
    font-weight:bold !important;
    background-color:#326432!important;
    border-color:#FEFEFE!important;
    color:#FFFFFF!important;
    padding-top:0.1850em!important;
    border-radius:10px 0 0 10px!important;
    vertical-align:top!important;
    text-align:right!important;
}
.captcha {
    color:#404040 !important;
}
textarea, [name="field4"] {
background:rgba(255,255,255,0.5) url("http://02ch.net/wi/src/1372912690177.png")!important;
}

/* DELFORM */
.omittedposts {
    color:#666666 !important;
}
.doubledash {
    display: none !important;
}

/* image */
.image { 
    border: none !important;
    float: left !important;
    margin: 2px 20px !important;
}
.nothumb {
    background-color: #FFFFFF !important;
    border-style: dotted !important;
    margin: .3em .5em !important;
}

/* postinfo */
.postername,.commentpostername{
    font-size:125%!important;
/*    text-shadow:5px 5px 10px #000000!important;*/
}
.postername a[href="mailto:sage"]:after,
.commentpostername a[href="mailto:sage"]:after{
    content:" SAGERU!"!important;
}
.reflink {
    font-size: .8em !important;
    font-weight: bold !important;
}
.filesize {
    font:100%/1.4 Helvetica,Arial,sans-serif!important;
}
.filetitle {
    font-size:150%!important;
    font-weight:200!important;
    display:block!important;
    clear:right!important;
}
.replytitle {
    font-size: 1.2em !important;
    color:#002244 !important;
    font-weight:bold !important;
}
.postertrip {
    color:#000000 !important;
    opacity:0.5!important;
}
.thumbnailmsg {
    font-size: .6em !important;
    color:#666666 !important;
}

/* post */
.reply {
    color:#333333!important;
    background-color:rgba(81,180,115,0.85)!important;
    border-color:#FEFEFE!important;
    border:1px solid #FEFEFE !important;
    padding:0 30px 20px 0!important;
    margin: 0 !important;
    border-radius:0 40px!important;
}
.unkfunc {
    background:inherit !important;
    color:#F9E9D2 !important;
    font-style:italic!important;
    margin-left:1em!important;
}
.oldpost, .notabene {
    color:#CC1105 !important;
    font-weight:bold !important;
}
blockquote {
    font-weight:600 !important;
    background-color:rgba(81,180,115,0.85) !important;
    border-bottom-color:#FEFEFE !important;
    border-left-color:#FEFEFE !important;
    border-right-color:#FEFEFE !important;
    border-top-color:#FEFEFE !important;
    border:1px solid currentColor !important;
    border-radius:0 40px !important;
    padding:20px 30px 20px 7px !important;
}
.reply blockquote {
    margin: .5em !important;
    border:0px !important;
    background-color:transparent !important;
}
.abbrev {
    color:#666666 !important;
}
.highlight {
    background:#EEDACB !important;
    color:#333333 !important;
    border: 2px dashed #EE6600 !important;
}
.underline {
    text-decoration: underline !important;
}
.red {
    color:#ff0000 !important;
}
b, strong {
    color:#007700 !important;
}

/* spoiler */
.spoiler {
    background: #007700 !important;
    color: #007700 !important;
}
.spoiler blockquote {
    background: #007700 !important;
    color: #007700 !important;
}
.spoiler:hover {
    background: #6BBF88 !important;
    color: #333333 !important;
}
.spoiler blockquote:hover {
    color:#789922 !important;
}
.spoiler blockquote blockquote:hover {
    color:#406010 !important;
}
.spoiler blockquote blockquote blockquote:hover {
    color:#204010 !important;
}

/* FOOTER */
.footer {
    text-align:center !important;
    font-size:12px !important;
    font-family:serif !important;
    margin: 1em 0 0 0 !important;
}
html>body>table>tbody>tr>td, 
html>body>table>tbody>tr>th {
    opacity:0.50 !important;
    border-radius:10px !important;
}
html>body>table>tbody>tr>td {
    padding:0.25em 0!important;
}
html>body>table>tbody>tr>td:hover, 
html>body>table>tbody>tr>td:focus, 
html>body>table>tbody>tr>th:hover {
    opacity:1.00!important;
}

/* FRAME */
dl.menu dt {
    background-color:rgba(81,180,115,0.85) !important;
    border: solid 1px #CCCCCC !important;
    border-radius:5px !important;
    -moz-border-radius: 5px !important;
    -webkit-border-radius:5px !important;
    margin-top: 1em !important;
    padding-left: .5em !important;
    cursor: pointer !important;
}
dl.menu dd {
    margin-left: .5em !important;
    padding-left: .5em !important;
    border-left: solid 1px #CCCCCC !important;
}
center {
    display:none !important;
}

/* ??? */
.counter {
    width: 50px !important;
    font-weight: bold !important;
    color:#FF0000 !important;
} 
.sepblock table {
    margin: 0px !important;
    text-align:left !important;
    width:100%
}
.separator {
    background:#FFFFFF !important;
    border-top:1px solid #BBBBBB !important;
    font-size:1px !important;
    width:100% !important;
    padding:0 !important;
    margin:0 !important;
    height:1px !important;
    overflow:hidden !important;
}
.dellist {
    font-weight: bold !important;
    text-align:center !important;
}
.delbuttons {
    text-align:center !important;
    padding-bottom:4px !important;
}
.managehead {
    background:#27815D !important;
    color:#002244 !important;
    padding:0px !important;
}
.postlists {
    background:#FFFFFF !important;
    width:100% !important;
    padding:0px !important;
    color:#800000 !important;
}
.row1 {
    background:#27815D !important;
    color:#002244 !important;
}
.row2 {
    background:#FFFFFF !important;
    color:#002244 !important;
}

/* WATCHERS */
[class^="watchers"] {
    display: none !important;
}
.list {
    background-color:rgba(255,255,255,0.5) !important;
}

}
