@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

.user-list a, .user-list .name-verified, .user-list .description {color: white !important; }

div.flex-module {background: transparent !important;}
div.flex-module {
        background:rgba(0, 0, 255, 0.4) !important
    }


                                /* full-width */
                                
    .dashboard { display: none !important; } 


 .content-main { width: 777px !important; }
.profile-card.profile-header {
    margin-left: auto !important;
    margin-right: auto !important;
    float: none !important;
}

    body {
    background-color: rgba(0, 0, 255, 0.9) !important;
    background-image: url("http://mobilewallpapers.narod.ru/newyear/images/snezhinki.jpg") !important;
    }
    
    
    .global-nav {background:rgba(0, 0, 255, 0.4) !important;}
    
    .tweet {
    border-radius: 1px !important;
    border-color: white !important;
    border: 5px  !important;
    }
    
    div.module.signup-call-out, div.view-more-container, div.tweet.permalink-tweet.js-actionable-user.js-actionable-tweet.with-social-proof.opened-tweet.animating,
    div.simple-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.with-social-proof,
    div.simple-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable,
    div.original-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-tweet,
    div.simple-tweet.tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.opened-tweet.animating,
    div.tweet.permalink-tweet.js-actionable-user.js-actionable-tweet.with-social-proof.opened-tweet,
    div.tweet.original-tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-tweet.with-social-proof,
    div.tweet.original-tweet.js-stream-tweet.js-actionable-tweet.js-profile-popup-actionable.js-original-twee,
    div.stream-end-inner,.stream-end-inner,.stream-loading 
    div.replies.js-conversation-replies,  
    .tweet-form.condensed div.rich-editor,
    .inline-reply-tweetbox.swift .tweet-box {
    background: linear-gradient(to top, rgba(0, 0, 255, 0.4), rgba(0, 0, 255, 0.5) ,rgba(0, 0, 255, 0.7), rgba(0, 0, 255, 0.8),  rgba(0, 0, 255, 0.4) 95% ,rgba(0, 0, 255, 0.4) 99% ) !important;}
    
    
    
   
#page-container {background-color:rgba(0, 0, 255, 0.4) !important}

/*.wrapper-profile .profile-card.profile-header .profile-header-inner {background:000 !important}*/
.wrapper-profile .profile-card.profile-header .profile-header-inner {
    background-color:rgba(0, 0, 255, 0.4) !important;
    border-color: black !important;
    border-radius: 7px !important;
    }
    
.flex-module.profile-banner-footer.clearfix {background:000 !important}



.profile-card.profile-header .profile-header-inner {
opacity: 1 !important;
background-color:rgba(0, 0, 255, 0.1) !important;
}

    div.flex-module {
border-right-style: solid;
border-right-color: #e2e2e2;
border-right-width: 2px;
}
    



    
    
    
    
    
    
    div.holding.name input {
        background:rgba(0, 0, 255, 0.4) !important
    }
    
    div.holding.email input.email-input {
        background:rgba(0, 0, 255, 0.4) !important
    }
    
    div.holding.password
    input
   {
        background:rgba(0, 0, 255, 0.4) !important
    }
    
    a.list-link.js-nav {background:rgba(0, 0, 255, 0.4) !important;}
    
    div.header-inner { background:rgba(0,0,0,0.05) !important
    }
    
    
    
    
    
    a,li {color:white !important;
    /*text-shadow:  1px 1px 2em white !important;*/
    text-shadow:  none !important;
    }
    
    h3,h2,small {color:white !important; font-size:79% !important;
   /* text-shadow:  3px 3px 4em red !important;*/
   text-shadow:  none !important;
    }
    
    a:hover > strong   {color:white !important; text-decoration:underline !important; }

    b {color:white !important;}
    s {color: white !important;}
    span {color: white !important; /*font-weight: bold !important;*/}
    p {color: white !important; font-size: 90% !important; font-weight: 900 !important;
   /* text-shadow: 2px 2px 4px #fff, 1px 1px 4em red !important; */
    }
    
    .spinner {display:none !important;}
    
    .inline-reply-tweetbox .tweet-box {color: white !important;}
    
    
    .geo-picker .dropdown-menu p {color: white !important;}
    
    .profile-card .stats {color: white !important; font-size: 90% !important; font-weight: 900 !important;
    }
    
    .wrapper-profile .profile-card.profile-header .profile-header-inner {color: black !important; font-size: 90% !important;
    font-weight: 900 !important;
    /*text-shadow: 2px 2px 4px #fff, 1px 1px 4em red !important;*/
    }
    
    
    .profile-card.profile-header .bio-container {font-size: 135% !important;
    }
     p.bio.profile-field {
     text-shadow: 2px 2px 4px #fff, 1px 1px 4em red !important;
     }





    
    div.flex-module.profile-banner-footer.clearfix {
width: 555px !important;
position:relative !important;
top: 5px !important;
right: 0px !important;
z-index: 1 !important;
    }
    .follow-button, .user-dropdown, .dm-button  {
    background: 
    linear-gradient(to top, rgba(0, 0, 255, 0.8), rgba(0, 0, 255, 0.4) ,rgba(0, 0, 255, 0.8),
    rgba(0, 0, 255, 0.4),  rgba(0, 0, 255, 0.8) 95% ,rgba(0, 0, 255,1) 99% ) !important;
    }
    .follow-button:hover, .user-dropdown:hover {
    background: 
    linear-gradient(to top, rgb(0, 0, 0),rgba(0, 0, 255, 0.4) ,rgba(0, 0, 255, 0.8),
    rgba(0, 0, 255, 0.4),  rgba(0, 0, 255, 0.8) 95% , rgb(0, 0, 0) 99% ) !important;
    }
    
    .dashboard div.flex-module {
    background: linear-gradient(to top, rgb(255, 255, 255), rgba(0, 0, 255, 0.4) ,rgba(0, 0, 255, 0.8), rgba(0, 0, 255, 0.4),  rgba(0, 0, 255, 0.8) 95% , rgb(255, 255, 255) 99% ) !important;
    }
    
    div.flex-module.media-thumbnails.large.recent_photos {
    background: linear-gradient(to top, rgb(255, 255, 255), rgba(0, 0, 255, 0.4) ,rgba(0, 0, 255, 0.8), rgba(0, 0, 255, 0.4),  rgba(0, 0, 255, 0.8) 95% , rgb(255, 255, 255) 99% ) !important;
    }
     div.module.profile-nav {opacity:1 !important; z-index: 99 !important;/*display:none!important;*/ background:#000 none !important;}
    
    span.location.profile-field {
    text-shadow: 2px 2px 4px white, 2px 2px 1em red !important;
    }
    
    body {margin: 0px 250px !important;}
    
    
}
