css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('feedly.com') {
.entryBody, BODY {transition: background .5s;font-family:'Corbel', sans-serif!important;overflow-y: auto!important;}

.content br:-moz-any(:-moz-first-node, :-moz-last-node),.content :not(img):not(br):-moz-only-whitespace, .content :-moz-any(p,a,div,td):empty {display:none!important}
.promo,.profileName + *, [data-uri*="pro/"],  .content a+a[title*="Tweet"]+div, .content br+br, .content div+br, .profeature,[data-uri="pro"], .u100Entry .bottomWikiWidget, .entryBody + *,#feedlyMessageBar, #feedlyBacksplashPart, .u5Entry .visual:not([style*="background"]), #tipsModule_part {display:none!important;}

.content br {line-height:1ex!important;}
.target:hover {background-color:hsla(0,0%,70%,.1)!important;}
div {border-radius:0!important;}
#searchBox,.u5Entry[style*="none"] {display:block!important;opacity:.5!important;}
.u5Entry:not(:hover) .wikibar span {opacity:0!important;}
.u5Entry, .wikibar span {transition:.4s opacity;}
.label, h1,.entryBody h2, .title {font-family:"segoe UI", sans-serif!important;letter-spacing:normal!important; font-weight:600!important;}

#feedlyTabs:not(:hover) {overflow:hidden!important;}
#feedlyTabs {padding-top:1rem;}
#feedlyTabs > div:first-child {position:absolute!important;bottom:1rem;right:3rem;z-index:1;}
.u5Entry {margin:0 .7rem 1.5rem .7rem!important;/*overflow:hidden!important;*/border:1px solid hsla(0,0%,50%,.14)!important; }  
.u5Entry:not(.textOnly) .info {max-height: 39%;} .u5Entry .info {max-height: 89%;}
.u5Entry .info,
.u5Entry > div {height:100%!important;padding:0!important;position:relative!important;}
.u5Entry .info + div {position:absolute;bottom:0;left:0;background:hsla(0,0%,100%,0)!important;}
.u5Entry .info + div,
.u5Entry .info > *:not(.visual) {margin:0!important;padding: .25rem 5% !important;width:90%!important;height:auto!important;}
.u5Entry .title {background:none!important; line-height:1.2!important}
.textOnly .title, .u5Entry .visual {color:inherit!important;margin:0!important;}

#feedlyPage, .wikiWidget {position: relative!important;} /*, .area*/
.selectedEntry:not(.u100Frame) {position:static!important;}
.entryBody, embed, iframe {line-height:1.42!important;width:100%!important;max-width:none!important;}
iframe, embed, #floatingEntry img[style*="block"] {text-align:center!important;margin:.5rem auto!important;}
  
#feedlyPart0 {animation: none!important;}
.sliderWidth {width:50vw!important;min-width:595px!important; }
.entryholder {background:inherit!important;}
.floatingEntryContentSlide {/*padding: 18px 0 !important;*/}
.sliderContainer {min-width: 645px!important;
margin:0 auto!important;left:0!important;right:0!important; top:6vh!important;}
.bumperHolder {top:50vh!important;}
.sliderContainer,.floatingEntryScroller {max-height:88vh!important;
width:60vw!important;min-width: 645px!important;}
.previewSlide-frameHolder > iframe {margin:0!important;}
  
#quicklookContent {border:0!important;margin:0!important;height:100%!important;width:100%!important;}
#quicklookFramePart {border:0!important;position: fixed!important;overflow:visible!important;height:80vh!important;top:8vh!important;width:80vw!important;left:10vw!important;}
#quicklookHeader {padding:8px 1%!important;position: relative!important}
}
