css:
@-moz-document domain("feedly.com") {
/* full width layouts */
#feedlyPage,
#feedlyFrame { width: auto !IMPORTANT; }
#feedlyPart { float: none !IMPORTANT; padding-right: 0 !Important; }

.section .label div:empty { display: none; }
.infobox { padding: 0 1em !important; }

/* headings */
#feedlyPageHeader { margin: 0 !important; padding: 0.75em !important; }
#feedlyTitleBar, #feedlyTitleBar a { font-weight: bold !important; font-size: 1rem !important; 	height: auto !important; }
#section0 { padding: 0; }
.subscription h2 { margin-left: 25px !important; }
.section h2, #categoriesArea h2 { margin: 0 !important; padding: 1rem !important; }  

/* left feeds sidebar*/ 
#feedlyTabs { padding-left: 0 !important; padding-right: 3px !important; width: auto !important; }
#navSelector_my { margin-left: 0 !important; }
.nonEmpty .feedtitle, .feedunreadcount { font-weight: bold !important; }
.tab .header { border-left: 3px solid rgba(0, 0, 0, 0); }

/* right side modules */
#sideArea { display: none !important; }

/* floating bar */
#floatingBar h1 { border-bottom: none !important; font-weight: bold !important; }
h1 .hhint { display: inline !important; }
#feedlyPageHeader .hAction { padding-top: 5px !important; 	
  	padding-left: 7px !important; padding-right: 7px !important; 
  	padding-bottom: 5px !important; }

/* links */
.entryBody a { text-decoration: underline; }

/* ARTICLES ======================================== */
/* --- article area */
#feedlyPart0.area { padding: 0 !Important; }

/* Entry frames */
.u100Entry,
.entrylist .selectedEntry { padding: 0 !important; max-width: none !important; }
.inlineFrame { border-left: none !important; margin-top: 0 !important; min-height: 0 !important; }
.u100Frame,
.condensed .entryholder .u100entry { padding: 0 !IMPORTANT; margin: 0 !important; }

/* entry titles */
.u100Entry .title { padding: 0.4rem 1rem !important; font-size: 1.1em !important; 
  	line-height: normal !important; font-weight: bold !IMPORTANT; 
  	max-width: 100% !important; margin: 0 !important; }
.title.read { font-weight: bold !important; }

/* entry header & metadata */
.entryHeader .metadata { padding: 0.5em 1em 0.3em !important; line-height: 1.5em !IMPORTANT; }
.sourceTitle { font-weight: bold !IMPORTANT; line-height: 1.3 !important; }

/* entry body & content */
.entryBody { padding: 0 1em !important; line-height: 1.3 !IMPORTANT; width: auto !important; 
  	max-width: none !IMPORTANT; margin-bottom: 1em !important; }
.entrybody img { float: none !important; max-width: 90%; }
.content blockquote { border-left: 4px solid lightgray; margin: 1em; 
  	padding-left: 1em; }
/* --- video margins */
.entrybody .content iframe { margin: auto !important; } 

/* title view */
.u0EntryList { margin: 0 !important; }
.u0Entry,
.condensed .inlineFrame { margin-left: 0 !important; }
.entryList .inlineFrame { padding: 0 !important; }
.u0Entry .sourceInfo { width: 180px !important; }
.u0Entry .u0Summary { padding-left: 28px !important; }
.u0Entry .title { white-space: nowrap !important; 
  	text-overflow: ellipsis !important; display: block !important; 
  	overflow: hidden !important; height: 30px !important; }


/* magazine & cards view */
.column { padding: 2em 1% 1em 2% !important; width: 30% !important; 
  	display: inline-block !important; }
.u4Entry { margin: 2em; max-width: none !important; }
.u5entry,
.u5entry .visual { width: auto !important; }
.u4Entry .metadata,
.u5Entry .metadata { padding: 0.5em 0 !important; background: transparent !important; max-height: none !important;}
.u4Entry .title, 
.u5Entry .title { font-weight: bold !important; line-height: 1.25 !important; }
.u4Entry .summary { max-height: 50px !important; }
.u5Entry .summary { max-height: 52px !important; }
.u5Entry .why { padding: 0.1em 0.2em !important; }
.u5Entrylist .frameActionsTop { margin: 0px!important; width: auto !important; position: relative; font-size: 12px !important; height: auto !important; line-height: normal !important; }
.u5Entrylist .frameActionsTop:before { content: attr(title); }


/* saved */
.why { border-radius: 0.2em !important; 
  	padding: 0.4em 0.5em 0.4em!IMPORTANT; line-height: 1.2!IMPORTANT; 	
  	float: right!IMPORTANT;}

/* tags */
.tagged, 
.tagged .tagLabel  { max-width: none !important; border: none !important; }
.tagged, 
.toBeTagged { padding-top: 0 !important; }

/* bottom-of-page stuff */
#aboutArea { margin-top: 0!important; }
#bigMarkAllAsReadButton { width: auto !important; }
.secondaryCallForAction { margin-right: 0 !important; }


/* wikiwidget */
.bottomWikiWidget { margin-top: 0em !important; padding: 0.5em 1em !important; }
.topWikiWidget { padding: 0.5rem !IMPORTANT; margin-top: 0 !important; }
/* adjust sharing widgets */
.entryholder .wikiwidget div[style*="margin-left"] { float: none !important; display: inline !important; }
.entryholder .wikiwidget div[style*="height"] { height: auto !important; }


/* site-wide font changes */
/* --- font family */
.u100Entry .title,
.entryBody h3 { font-family: helvetica,arial,sans-serif !important; 
  	font-weight: bold !important; }

* {font-family: helvetica,arial,sans-serif !important;}



/* --- font sizes */
.hhint,
.infoBox,
.summary,
.u0Entry .title,
.u0Entry .sourcetitle a,
.u0Summary,
#floatingBar h1 { font-size: 16px !important; }

.u5Entry .summary,
.u5Entry .title,
.metadata *.action,
.why { font-size: 14px !important; line-height: 1.25em !important; }

.entryBody,
.u100Entry .metadata { font-size: 1rem !important; }






/* ---Colour rules -------------------------------------------------------------- */

/* white background */
#floatingBar, #floatingBar h1,
#feedlyPageHeader, .section h2, #categoriesArea h2, .bottomWikiWidget, 
.u4Entry .metadata, .u5Entry .metadata, .u5entrylist .frameactionstop, .entryHeader,
.websitecallforaction, .u0Entry.quicklisted { background-color: white !important; }

/* gray background */
#feedlyTabs .tab .target:hover { background-color: rgba(0,0,0,0.1); }
h1 .hAction, .bigMarkAsReadButton,
.u100Entry .title, .u0Entry.quicklisted:hover { background-color: whitesmoke !important; }
h1 .hAction:hover, .tagged { background-color: gainsboro !important; }

/* blue background */
.why, #save, #tagCreator div, .formContainer .actionButton, .inlineFormContainer .actionButton { background-color: royalblue !IMPORTANT; }

/* gray border */
#feedlyPageHeader, #floatingBar, .u5entrylist .frameactionstop { border-bottom: 1px solid silver; }
.inlineFrame { border: 1px solid #BBB !important; }
.u100Frame { border-top: 1px solid silver !IMPORTANT; }
.u100Frame, .u0Entry { border-left: 4px solid gainsboro !important; }
.bottomWikiWidget, .websitecallforaction {  border-top: 1px solid gainsboro; }
.u0Entry:last-child { border-bottom: 1px solid #efefef; }

/* blue border  */
.selectedEntry { border-left: 4px solid cornflowerblue !IMPORTANT; }

/* red border */
.tab .header.selected { border-left: 4px solid crimson; }

/* black text colour */
#feedlyTabsHolder, .read, .cell h2, .entryBody,  a.title, .u0Entry .title, .u100Entry .title, #floatingBar h1, #feedlyTitleBar, #feedlyTitleBar a, a.title, .tagged { color: black !important;  }
  
/* gray text colour  */
#feedlyTitleBar span, h1 .hhint, .categoryUnreadCountHint span, #floatingBar span[style*=color], .section h2, #categoriesArea h2, .entryHeader .metadata, .entryHeader .sourceTitle, .u4Entry .metadata, .u5Entry .metadata, .u5Entry .sourceTitle, .u0Entry .sourceTitle a, .infobox, .u5entrylist .frameactionstop { color: gray !important; }

/* blue text colour */
a, .entryBody a, .action, .formContainer .secondaryActionLink, .inlineFormContainer .secondaryActionLink, .transparentFormContainer .secondaryActionLink { color: royalblue !important; }

/* white text colour */
.why, #save { color: white!IMPORTANT; }

h1 .hAction { top: auto !important; }
h1 .hAction:hover { text-decoration: none !important; }




	
	

/* RESPONSIVE FEEDLY ===========================================*/

/*  feeds panel always visible until window is less than 600px */
/*
@media screen and (min-width: 600px)  {
.narrow #mainBar { margin-left: 260px !important;} 
.narrow #feedlyTabsHolder { width: 260px !important; background-image: none !important; }
.narrow .panels,
.narrow #feedlyTabs { opacity: 1 !important; visibility: visible !important; }
}
*/


@media screen and (max-width: 640px)  {
#feedlyTitleBar { clear: both !important; }
  .pageActionBar {
float: none  !important;
margin-bottom: 5px !important;
margin-left: -7px !important;
}
  
.entrybody .content img { 
max-width: 80vw !important;
height: auto !important; } 

/* 2-column card view */
.column { width: 45% !important; display: block !important; }
.u5Entrylist .inlineFrame { width: auto !important; } 


}

@media screen and (max-width: 500px) {
/* single column card view */
  .column { width: 90% !important; }
}
}
