css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http(|s)://(|www.)2(|-)ch\.(so|ru|hk|ec|pm|re|tf|wf|yt).*") {
  * {
    padding: 0;
    margin: 0;
    /*border: 1px solid rgba(255,67,67,0.5)*/
    font-family: Ubuntu, Tahoma !important;
  }
  html {
    width: 768px;
    margin: 0 auto !important;
    background: #99A !important;
    padding: 0 !important;
  }
  
  body {
    background: rgba(0,0,0,0) !important;
    /*box-shadow: 0 0 19px #000*/
    /*outline: 4px solid rgba(0,0,0,0.3);*/
    padding: 0 !important;
  }
  
  #adminbar, #boardNav {
    display: none;
  }
  
  .turnmeoff {
    opacity: 0;
  }
  
  #title {
    color: #AA7777;
    margin-bottom: 20px !important;
  }
  
  .post, .reply, tbody, tr {
    background: rgba(0,0,0,0.0) !important;
    width: 748px !important; 
    display: block !important;
    border: 0 !important;
    border-radius: 0px !important;
    color: #444;
    text-shadow: 1px 1px 0 rgba(255,255,255,0);
  }
  
  .post {
    background: #ebe6e6 !important;
    border: 1px solid rgba(0,0,0,0.4) !important;
    box-shadow: inset 0px 1px 1px 0px rgba(255,255,255,0.6), 
                inset 0px -1px 0px 0px rgba(0,0,0,0.3) !important;
    height: 1%; overflow: hidden !important;
    margin: 10px 0 0 10px !important;
    padding: 10px !important;
    
  }
  
  .oppost {
    display: block !important;
    height: 1%; overflow: hidden;
    position: relative !important;
    color: #444;
    background: #e6e6eb !important;
    border-radius: 0px !important;
    border: 1px solid rgba(0,0,0,0.4) !important;
    border-left: 0 !important;
    border-top: 0 !important;
    box-shadow: inset -1px 1px 1px 0px rgba(255,255,255,0.4), 
                inset 0px -1px 0px 0px rgba(0,0,0,0.3) !important;
    padding: 10px !important;
    margin: 0 !important;
  }
  
  .post:nth-child(2n) {
    background: #ebe8e6 !important;
  }
  .post:nth-child(3n) {
    background: #ebebe6 !important;
  }
  .post:nth-child(4n) {
    background: #e6ebe6 !important;
  }
  .post:nth-child(5n) {
    background: #e6e6eb !important;
  }
  .post:nth-child(6n) {
    background: #e9e6eb !important;
  }
  

  
  .omittedposts {
    position: relative !important;
    bottom: 0px;
    margin: 5px !important;
    right: 0px;
    font-size: 0.75em;
    float: right !important;
    display: inline-block !important;
  }
  
  hr {
    display: none !important;
  }
  
  pre {
    border: 1px groove rgba(255,255,255,0.07) !important;
    background-color: rgba(22,22,22,1) !important;
    color: rgba(210,80,80,1) !important;
    display: inline-block !important;
    padding: 8px 13px !important;
    box-shadow: 0px 3px 5px 0px #000 !important;
  }
  
  .de-pview, .de-post {
    border: 1px solid rgba(0,0,0,0.5) !important;
    background-color: #dedddf !important;
    border-radius: 5px !important;
    display: inline-block !important;
    padding: 8px 13px !important;
    box-shadow: inset 0px 1px 1px 0px rgba(255,255,255,0.6), 
                inset 0px -1px 0px 0px rgba(0,0,0,0.3) !important;
    width: 600px !important;
  }
  
  a {
    color: #883939 !important;
  }
        
  .unkfunc {
    color: #338833 !important;
  } 
  
  .posttime, .postername, .de-ppanel, .reflink, .filetitle, .replynomobile {
    position: relative;
    left: -25px !important;
    top: -5px !important;
    font-size: 0.95em;
    color: transparent !important;
    text-shadow: 0px 0px 1px #222;
    opacity: 0.3;
  }
  
  .de-ppanel-cnt {
    color: transparent !important; 
    text-shadow: 0px 0px 0px transparent;
  }
  
  .reflink a {
    color: transparent !important;
    text-shadow: 0px 0px 1px #922;
  }
  .posttime:hover, .postername:hover, .de-ppanel, .reflink:hover, .filetitle:hover {
    text-shadow: 0px 0px 0px #333;
    opacity: 1;
  }
  .reflink a:hover {
    color: transparent !important;
    text-shadow: 0px 0px 0px #922;
  }
  
  .de-ppanel {
    opacity: 0.2;
  }
  .de-ppanel:hover {
    opacity: 1;
    text-shadow: 0px 0px 0px #333;
  }
  
  .de-btn-src, .filetitle, .thumbnailmsg, .img, .turnmeoff, .filesize {
    /*display: none !important;*/
    /*margin: 0 !important;*/
  }
  
  .thumbnailmsg {
    display: none;
  }
  
  
  .img {
    position: relative !important;
    max-width: 150px !important;
    border-top: 1px solid rgba(255,255,255,0.6) !important;
    border-bottom: 1px solid rgba(0,0,0,1) !important;
    height: auto !important;
    border-radius: 5px;
  }
  
  /*.thread:before {
    content: '';
    height: 100%;
    width: 10px;
    background: #e6e6eb !important;
    position: absolute;
    border-radius: 5px;
    padding: 0 !important;
  }*/
  
   .thread {
    border-left: 10px solid #e6e6eb !important;
    border-top-left-radius: 5px;
    padding: 0 !important;
    margin-bottom: 30px !important;
    /*border-top: 1px solid rgba(0,0,0,0.4) !important;*/
    /*box-shadow: inset 0px 1px 1px 0px rgba(255,255,255,0.6), 
                inset 0px -1px 0px 0px rgba(0,0,0,0.3) !important;*/
  }
  
  .post:last-child {
    margin-bottom: 0 !important;
  }
  
  .filesize {
    padding: 15px !important;
  }
  
  .filetitle {
    color: #222 !important;
    text-shadow: 0px 0px 0px #FFF !important;
    opacity: 0.5;
  }
  .filetitle:hover {
    color: #222 !important;
    text-shadow: 0px 0px 0px #FFF !important;
    opacity: 1;
  }
}
