css:
/*
 * TV Tropes colorful links
 *  https://userstyles.org/styles/90161
 * Created by AndreasSE
 *  https://userstyles.org/users/58590
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtropes.org") {
	.twikilink:link {
		color: mediumBlue !important;
	}
	.twikilink:visited {
		color: blueViolet !important;
	}
	.twikilink:link:hover {
		color: blue !important;
	}
	.twikilink:visited:hover {
		color: darkViolet !important;
	}
	.twikilink:link:focus {
		color: slateBlue !important;
	}
	.twikilink:visited:focus {
		color: darkOrchid !important;
	}
	.spoiler :link,
	.spoiler :visited,
	.spoiler :link:hover,
	.spoiler :visited:hover,
	.spoiler :link:focus,
	.spoiler :visited:focus {
		color: inherit !important;	/* Don't color links in spoilers */
	}
	
	.spoiler::selection,
	.spoiler ::selection {
		color: black !important;
		background: lavender !important;
	}
	.spoiler .twikilink:link::selection {
		color: mediumBlue !important;	/* Make it possible to spot links in selected spoilers */
	}
	.spoiler .twikilink:visited::selection {
		color: blueViolet !important;
	}
	.spoiler .twikilink:link:hover::selection {
		color: blue !important;
	}
	.spoiler .twikilink:visited:hover::selection {
		color: darkViolet !important;
	}
	.spoiler .createlink::selection {
		color: #8C0B0B !important;
	}
	
	.spoiler::-moz-selection,
	.spoiler ::-moz-selection {
		color: black !important;
		background: lavender !important;
	}
	.spoiler .twikilink:link::-moz-selection {
		color: mediumBlue !important;	/* Make it possible to spot links in selected spoilers */
	}
	.spoiler .twikilink:visited::-moz-selection {
		color: blueViolet !important;
	}
	.spoiler .twikilink:link:hover::-moz-selection {
		color: blue !important;
	}
	.spoiler .twikilink:visited:hover::-moz-selection {
		color: darkViolet !important;
	}
	.spoiler .createlink::-moz-selection {
		color: #8C0B0B !important;
	}
}
