css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("02ch.net") {

/* GLOBAL */
html, body { 
    color: #333333 !important;
}
html {
    background-color: #BBBBBB !important;
    font-size:10pt !important;
    font-family: "Trebuchet MS",Trebuchet,serif !important;
    padding-left: 23px !important;
    padding-right: 23px !important;
}
body {
    border:0 !important; 
    box-shadow: 0px 0px 5px #000000 !important;
    background:#EEEEEE url("http://02ch.net/wi/src/1373168734843.png") repeat-x 0 0 !important;

}
a {
    color: #FF6600 !important;
}
a:visited {
    color: #FF6600 !important;
}
a:hover {
    color: #0066FF !important;
}
hr {
    border-style: solid none none none !important;
    border-width: 1px !important;
    border-color: #BBBBBB !important;
}

/* HEADER */
.adminbar {
    clear:both !important;
    float:right !important;
    font-size: .8em !important;
    position:fixed !important;
    top:0px !important;
    left:30px !important;
    border-left:1px #CCC solid !important;
    border-right:1px #CCC solid !important;
    border-bottom:1px #CCC solid !important;
    padding:3px !important;
    -moz-border-radius: 0px 0px 5px 5px !important;
    -webkit-border-bottom-left-radius: 5px !important;
    -webkit-border-bottom-right-radius: 5px !important;
    z-index:100 !important;
    background-color: #EEEEEE !important;
}
.adminbar a {
    font-weight: bold !important;
}
.logo {
    clear:both !important;
    text-align:left !important;
    font-size:2em !important;
    font-weight: bold !important;
    color:#FF6600 !important;
    /*width:100% !important;*/
    padding-top:50px !important;
}
.theader, .passvalid {
    background:#DDDDDD !important;
    text-align:center !important;
    padding:2px !important;
    color:#2266AA !important;
    clear: both !important;
    font-weight: bold !important;
    margin-bottom: .5em !important;
    border: solid 1px #CCCCCC !important;
    -moz-border-radius: 5px !important;
    -webkit-border-bottom-left-radius: 5px !important;
    -webkit-border-bottom-right-radius: 5px !important;
    -webkit-border-top-left-radius: 5px !important;
    -webkit-border-top-right-radius: 5px !important;
}

/* POSTFORM */
.postarea {
}
.postblock {
    background:transparent !important;
    color:#002244 !important;
    font-weight:bold !important;
}
.rules {
    font-size:0.7em !important;
}

/* THREAD */
.omittedposts {
    color:#666666 !important;
}
.doubledash {
    display: none !important;
    vertical-align:top !important;
    clear:both !important;
    float:left !important;
}
/* image */
.image { 
    border: none !important;
    float: left !important;
    margin: 2px 20px !important;
}
.nothumb {
    float: left !important;
    background: #eee !important;
    border: 2px dashed #aaa !important;
    text-align: center !important;
    margin: 2px 20px !important;
    padding: 1em 0.5em 1em 0.5em !important;
}
/* post info */
.filetitle {
    background:inherit !important;
    font-size:1.2em !important;
    color:#002244 !important;
    font-weight:bold !important;
}
.replytitle {
    font-size: 1.2em !important;
    color:#002244 !important;
    font-weight:bold !important;
}
.postername {
    color:#004A99 !important;
    font-weight:bold !important;
}
.commentpostername {
    color:#004A99 !important;
    font-weight:800 !important;
}
.postertrip {
    color:#FF3300 !important;
}
.reflink {
    font-size: .8em !important;
    font-weight: bold !important;
}
.thumbnailmsg {
    font-size: .8em !important;
    color:#666666 !important;
}
.filesize {
    text-decoration:none !important;
    color: #666666 !important;
    font-size: .8em !important;
}
/* post */
.reply {
    background:#DDDDDD !important;
    color: #333333 !important;
    border: solid 1px #CCCCCC !important;
    padding: 0 !important;
    margin: 0 !important;
    -moz-border-radius: 5px !important;
    -webkit-border-bottom-left-radius: 5px !important;
    -webkit-border-bottom-right-radius: 5px !important;
    -webkit-border-top-left-radius: 5px !important;
    -webkit-border-top-right-radius: 5px !important;
}
blockquote {
    margin: .5em .5em .5em 1em !important;
}
blockquote p {
    margin: 0 !important;
    padding: 0 !important;
}
.reply blockquote {
    margin: .5em !important;
}
.abbrev {
    color:#666666 !important;
}
.highlight {
    background:#EEDACB !important;
    color:#333333 !important;
    border: 2px dashed #EE6600 !important;
}
.unkfunc {
    background:inherit !important;
    color:#789922 !important;
}
.oldpost {
    color:#CC1105 !important;
    font-weight:bold !important;
}
[id^="pstprev"], .de-pview {
    position: absolute !important;
    /*background: #eee !important;*/
    margin: 0px !important;
    padding: 0px !important;
    border: 1px #666 dashed !important;
    -webkit-box-shadow: #666 0px 0px 10px !important;
    -moz-border-radius: 5px !important;
    -webkit-border-bottom-left-radius: 5px !important;
    -webkit-border-bottom-right-radius: 5px !important;
    -webkit-border-top-left-radius: 5px !important;
    -webkit-border-top-right-radius: 5px !important;
}
.reply blockquote, blockquote :last-child {
    margin-bottom: 0em !important;
}
.reflink a {
    color: #333333 !important;
    text-decoration: none !important;
}
.reflink a:hover{
    color: #800000 !important;
}
.reflink a:visited{
    color: #333333 !important;
}
.reply .filesize {
    margin-left: 20px !important;
}
/* spoiler */
.spoiler {
    color: black !important;
    background-color: black !important;
}
.spoiler a {    color: inherit !important;}
.spoiler a:visited {    color: inherit !important;}
.spoiler a:hover {  color: inherit !important;}

/* FOOTER */
.footer {
    text-align:center !important;
    font-size:12px !important;
    font-family:serif !important;
    margin: 2em 0 0 0 !important;
}
/* ??? */
.dellist {
    font-weight: bold !important;
    text-align:center !important;
}
.delbuttons {
    text-align:center !important;
    padding-bottom:4px !important;
}
.managehead {
    background:#DDDDDD !important;
    color:#002244 !important;
    padding:0px !important;
}
.postlists {
    background:#FFFFFF !important;
    width:100% !important;
    padding:0px !important;
    color:#800000 !important;
}
.row1 {
    background:#DDDDDD !important;
    color:#002244 !important;
}
.row2 {
    background:#CCCCCC !important;
    color:#002244 !important;
}

/* WATCHERS */
[class^="watchers"] {
    display: none !important;
}

/* --------------------*/
/* IMG-GLOBAL */
body {
    margin: 0 !important;
    padding: 8px !important;
    margin-bottom: auto !important;
}
blockquote blockquote {
    margin-left: 0em !important;
}
form {
    margin-bottom: 0px !important;
}
form .trap {
    display:none !important;
}
.postarea {
    text-align: center !important;
}
.postarea table {
    margin: 0px auto !important;
    text-align: left !important;
}
.code_full {
    border: #666666 dashed 1px !important;
    background: #EFEFEF !important;
}
pre {
    font-family: Consolas, Lucida Console, monospace !important;
    margin: 0em !important;
}
.code_part {
    border: #DFDFDF dashed 1px !important;
    background: #EFEFEF !important;
    width: auto !important;
    max-height: 400px !important;
    overflow: auto !important;
    padding:0px !important;
    margin:0px !important;
}
.userdelete {
    float: right !important;
    text-align: center !important;
    white-space: nowrap !important;
}
.replypage .replylink {
    display: none !important;
}

}
