css:
@-moz-document domain('dabr.appb.in'), domain('dabr.co.uk'),domain('citcuit.in'),domain('twa.sh'),url-prefix('http://scriptics.ir/dabr')  {


/* ==================== Fonts ==================== */

@font-face {
     font-family: 'Nasim';
     src: local('Nasim'),
     url('http://awebfont.ir/services/fonts/393b712c01cdf4eb7f15d9180567345060f101241011931.woff') format('woff');
font-weight: normal;
font-style: normal;
}

@font-face {
  font-family: 'Alegreya Sans';
  font-style: normal;
  font-weight: 300;
  src: local('Alegreya Sans'), local('AlegreyaSans-Regular'), url(https://themes.googleusercontent.com/static/fonts/alegreyasans/v1/KYNzioYhDai7mTMnx_gDgtUFqmyT-Ih543PWa_C0ig4.woff) format('woff');
}


* {
font-family: 'Alegreya Sans','Nasim',"tahoma"!important; /*'Ubuntu', 'B Mitra', 'B Yekan' ,"tahoma"!important;*/
/*font-size:18px !important;
text-decoration: none!important;*/
color: #222;
font:100% !important;
text-rendering: optimizeLegibility;
  -webkit-font-smoothing:antialiased;
  
}

/* ==================================== */


a {color: #624AC4  !important;}
strong {color: #686868}

.menu a {
  color:#000000 !important;
  transition:color 0.5s linear;
  }
.menu a:Hover {
   color:red !important;
  }
    
p {margin-right:0 !important;}
body {position:relative;}
body > p {
padding-right:15px;
padding-left:15px;
}
input[value="Save"] {
width:100px;
height:40px;
}

.status {
/* font-family:tahoma !important; 
font-size:12px; */
  font-size: 1.1em !important;
  line-height: 1.0 !important;
  color:black;
}

.shift {
/* font-family:tahoma !important; 
font-size:12px; */
  font-size: 1.25em !important;
  line-height: 1.0 !important;
  color:black;
}

.date {clear:both; color:#200772 !important;}
table + p {text-align:right !important; padding-right:20px;}

div.menu-bottom {color:black !important;}
div.menu-bottom + img, .advert {display:none}
div.menu-bottom + img + p {direction:ltr;
font-size:10px; color:green; position:absolute; left:10px; bottom:30px; display:none;}


span label#lblGeo {font-size:.9em; border:2px dashed green; padding:3px;}
label#lblGeo {display:none}

.about a, .features a{color: #609!important; }
.features a:Hover{color: red !important; }
.features {color: #609!important; }

.profile .avatar a img, .profile .avatar {width: 60px !important; height: 60px !important;}
.profile .avatar {margin:10px !important;}


.timeline,p {
direction:rtl;
text-align:right;
/* font-family:tahoma; */
}



.tweet {padding-right:25px !important;}
.avatar, .avatar img {width:38px !important; height:38px !important;}
.avatar {margin-left:10px !important;}
.avatar {display:none !important;}

.status b {
  /*color:#660099 !important;*/
  font-size: 0.85em !important;
  line-height: 1.0 !important;
  }

.status b a {
  color:#660099 !important;
  font-size: 0.85em !important;
  line-height: 1.0 !important;
  }
.status b a:Hover {
        color:#000000 !important;
        font-size: 0.85em !important;
 		line-height: 1.0 !important;
      }
.status a {
  color:#000000 !important;
  font-size: 0.85em !important;
  line-height: 1.0 !important;    
  }
.status [target="_blank"] , .tweet-url {color:#648013 !important;}
.status [target="_blank"]:hover , .tweet-url {color:red !important;}
.status small {color:#555!important}
.status small a{color:red !important}

a[accesskey="9"]{
  color:#0047FD !important; 
  font-weight:bold; 
  border:0px solid #88DBE3; 
  padding:2px; 
  background-color:none;}

.timeline + p {text-align:right;}
table {width:100%}
a{text-decoration:none !important}

.even{background:#F1F0FF!important;}
.odd, .even{border-bottom:1px dashed #CFCFCF;}
.odd {background: rgb(255, 255, 255) !important;}
body {background: #DFE4F5 !important;}
.menu{background: #C3C3E5!important; 
border-bottom:2px solid black;
border-top:7px solid #0a4306;
color:white !important;
}

span.from {
/* font-family:tahoma !important; */
/*font-size:10px;*/
  font-size: 0.75em !important;
  line-height: 1.0 !important;
  color:#555;
}
span.from a:Hover {
color:red !important;
}

a.username{
color:#8C489F !important;
}


#status, form[action="twitpic"] textarea, form[action="update"] {direction:rtl; text-align:right;}
form {display:block}
textarea#status, form[action="twitpic"] textarea, form[action="update"] textarea{
float:right; width:100% !important; height:65px;
/* font:12px tahoma,arial; */
box-sizing:border-box;
}
form[action="twitpic"], form[action="update"] {text-align:right; position:relative;}
.avatar {float:right!important; position:relative !important}

form div input {float:right; width:100px; height:65px;}
span#remaining {
float:right; color:#00454f;
 
-moz-box-shadow:0 0 5px #aaa;

box-shadow: 0 0 5px #aaa;

width:67px; height:52px; 
line-height:52px; 
text-align:center; 
font-size:1.7em !important;
margin:3px 30px 0;
}
#geo {
float:right;
position:relative;
right:110px;
top:0px;
}

form[action="twitpic"], form[action="update"]{
height:110px;
}
form[action="twitpic"] #remaining {
position:absolute;
top:35px;
right:500px;
}
form[action="update"] #remaining {
position:absolute;
top:25px;
right:500px;
}

form[action="twitpic"] input[type="submit"] {
width:100px;
height:70px;
float:right;
position:relative;
top:-17px;
}


*::-moz-selection  {
background-color:#090;
color:#FFFFFF;
}



.followers .avatar {
float:left !important;
margin:0 20px 0 0 !important;
}
.followers .status a {
color:#2698D1 !important;
font-weight:bold;
}

form[action="search"] {
width:380px;
height:80px;
margin: 0 auto !important;
line-height:5em;
}

form[action="search"] *{
line-height:2em;
vertical-align:middle;
height:30px;
/* font-family:tahoma, arial; */
direction:rtl;
}


.menu,.menu-top + p,.menu-top + p + div, body div + div div {
padding-left:10px !important;
padding-right:10px !important;
}
.menu-top + p {
margin-left:auto;
margin-right:auto;
width:400px;
direction:ltr;
}
.menu-top + p + div {
padding-bottom:20px;
}
.menu-top + p + div > span {
margin-right: 5px;
}


form[action="settings/save"] + hr + p, form[action="settings/save"] {
padding-left:20px;
}
form[action="settings/save"] *, form[action="settings/save"] + hr + p {
direction:ltr !important; text-align:left;
}

#about {
background-color:#F6FFD7 !important;
border:3px dashed gray;
}

#geo input {
width: 14px;
}

a[href^="hash"] { 
  color: rgb(136, 23, 13) !important; 
  /* font-size:12px; */
  font-size: 1.1em !important;
  line-height: 1.0 !important;
  font-style:italic;
}

}
