css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("videohelp.com") {

body 
{
background:#000 !important;
}
.listmenulist, .howtomenulist, .whatismenulist, .othermenulist,
.postrow, .has_after_content, .userinfo, .postdetails,
.posthead, .body_wrapper, .textcontrols, .postbody,
#thread_controls div, .spiffywhite5pxfg div,
.spiffywhite5pxfg, .navigation li
{
background:#000 !important;
}
.textcontrols
{
border-top: 1px #FFFFFF solid !important;
}
.sticky
{
border: 1px silver solid !important;
}
select, input
{
border: 1px #FFF solid !important;
}
.editor_controls
{
background:silver !important;
}
div.listmenulistover, div.othermenulistover, 
div.howtomenulistover, div.whatismenulistover,
.navigation li:hover, .quote_container,
.newcontent_textcontrol, .navlinks,
.navbit a:hover, .bbcode_code, .pagelinks a,
.nouser a, .blocksubhead
{
background:#222 !important;
}
.forumrow, .above_threadlist_controls,
.threadinfo, .inner, .threadstats,
.threadlastpost, .nonsticky, .sticky, 
.blockrow, .blockfoot, .nouser
{
background:#111 !important;
}
.maingray, .spiffywhite5px, .row1 a,
.row1, .row2, .row1green, .greenbox,
input, select, textarea
{
color:#FFF !important;
background:#000 !important;
}
a, p, h1, h2, h3, h4, h5, .shade, .forum_info, .date, blockquote, 
.time, body, .spiffywhite5pxfg, .foruminfo, .forumrow, .body_wrapper, .navlinks,
.wgo_subblock, .blocksubhead, .threadinfo, .threadlastpost, .threadstats, .greenbox,
.nonsticky, .above_threadlist_controls, .posthead, .postbody, .quote_container, label, #remember, #remember span,
.userinfo, .textcontrols, .postdetails,
.listmenulist a, .howtomenulist a, .whatismenulist a, 
.othermenulist a, .row1 font
{
color:#FFF !important;
}
}
