css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtorrents.com") {

/** DarkNow styles **/

html a {
color:#FFF !important;
}
html a:hover {
color:#bbb !important; /* a little brigehtr */
}

div.container{
margin:0 auto !important;
width:1005px !important;
}

div.left{
margin-right:10px !important;
}

div.middle{
position:static !important;
float:left !important;
left:auto !important;
}

div.right {
 display:none !important;
 }

div.right div[style="width: 100%;"]
{
   width:auto !important;
}

body {
background:#222 !important;
}
.menu {
background: black !important;
-webkit-border-radius: 10px;
border-radius: 0px;
}
.topbar {
background:black !important;
-webkit-border-radius: 10px;
border-radius: 10px;
margin:10px !important;
}

/* Head of MENU, SHOW etc boxes */
.head {  
background:black !important;
}
.body {
background:#333 !important;
}
.container > table > tbody > tr > td:first-child > a > img {
width: 0 !important;
padding-left: 496px !important; /* replace with width of new image */
height: 124px !important; /* replace with height of new image */
background: url("http://image.torrent-invites.com/images/368tvts.png") center no-repeat !important;
}
.head2, a.head2:link, a.head2:visited, a.head2:active, a.head2, .tvtbox .body {
color:#FFF !important;
}
.boxhead h2 {
background:#000 !important;
}
.boxbody{
color:#FFF !important;
background:#444 !important;
}
.boxbody td{
color:#FFF !important;
background:#222 !important;
}
font.big, span.big{
color:#FFF !important;
}
.schedule_fav{
color:#000 !important;
}
.subbox, .subbox a {
background:#000 !important;
}
 div.subbox img{
display:none !important;
}
.tvtbox{
border:2px black solid !important;
}


/* TVT Reloaded styles */

#dl_frame 
{
    display: none
}

.list_even 
{
    background-color: #ccc !important;
}

/*
div.lineitem {
   margin: 3px 3px;
   padding: 2px;
   color: black !important;
   background-color: black !important;
}
*/

hr 
{
     background-color: #999 !important;
}

.medium
{
    color: #ccc !important;
}

/* Various input field */ 
.combo_input, .combo_select, .tinput
{
    color: white !important;
    background-color: #555 !important;    
}



/** Top menu **/

/* html body div.container div.tvtbox div.menu
{
    height: 62px !important;
} */

/* Selected menu entries */
html body div.container div.tvtbox div.menu a[style="color: rgb(187, 34, 34);"],
html body div.container div.tvtbox div.menu div.submenu a#my_tagged_shows[style="color: rgb(187, 34, 34);"]
{
    color: #bbb !important
}

/* Search Show/Torrents forms */
html body div.container div.tvtbox div.menu div.submenu form.showsearch,
html body div.container div.tvtbox div.menu div.submenu form.torrentsearch

{
    margin-top: 2px !important;
}

/* Search Show/Torrent input field */
html body div.container div.tvtbox div.menu div.submenu form.torrentsearch input.tinput,
html body div.container div.tvtbox div.menu div.submenu form.showsearch input.tinput
{
    width: 137px !important;
}



/*** Public user profile page: Member Info box ***/
html body div.container div.middle span#body_span table tbody tr td div.tvtbox div.body table tbody tr.list_even 
{
    background-color: #333 !important;   
}






/**** LEFT NAVIGATION ****/

/* "Search shows" input field */
html body div.container div.left div.tvtbox div.body form.showsearch input.tinput
{
    border-color: #888 !important;
    margin-bottom: 7px !important;
}

/* submit buttons */
html body div.container div.left div.tvtbox div.body form.showsearch input.miscbutton2, 
.miscbutton2
{
    color: black !important;
    background-color: #bbb !important;
}

/* hide <hr> elements in left navigation */
html body div.container div.left div.tvtbox div.body hr
{
    visibility: hidden !important;
}

/* The "SHOWS" block */
html body div.container div.left div.tvtbox div.body
{
    padding: 9px 5px !important;
}

/* The "Search show" form */
html body div.container div.left div.tvtbox div.body form.showsearch
{
    margin-top: -12px !important;
}    

/* space between "Search show" input field and "GO" button */
html body div.container div.left div.tvtbox div.body form.showsearch input.tinput
{
    margin-top: 5px !important;
    margin-bottom: 4px !important;
}






/**** Home ****/


/** Home | Schedule **/

/* show name links in left column */
html body div.container div.middle span#body_span div.sidebox div.boxbody table tbody tr td table tbody tr.schedule td a.schedule
{
    color: white !important;
    background-color: #222 !important;    
}

/* underline hovered links */
html body div.container div.middle span#body_span div.sidebox div.boxbody table tbody tr td table tbody tr.schedule td a.schedule:hover
{
    text-decoration: underline !important;
}





/**** Shows ****/

/** Shows | Recently uploaded **/

/* Table header */
html body div.container div.middle span#body_span div.tvtbox div.body table tbody tr.rub
{
    background-color: #222 !important;    
}





/**** Torrents ****/

/** Torrents | Unseeded **/
html body div.container div.middle span#body_span div.tvtbox div.body table tbody tr.list_even
{
    background-color: #333 !important;
}





/**** My Pages ****/


/** MY Pages | My Tagged Shows **/

/* 
   MY Pages | My Tagged Shows
   Margins for the horizontal ruler and tagged show table 
   Potential conflicting rules in Home | Recently Uploaded Torrents table
*/
html body div.container div.middle span#body_span div.tvtbox div.body br + hr,
html body div.container div.middle span#body_span div.tvtbox div.body br + table
{
    margin-top: 15px !important;
    margin-bottom: 15px !important;    
}
/* 
   My Pages | My Tagged Shows 
   Discontuned shows
*/
html body div.container div.middle span#body_span div.tvtbox div.body table tbody tr td[style*="background-color:#CCCCCC;"]
{
    background-color: #111 !important;
}


/** My Pages | New Fav Torrents **/

/* Resetter for the table that has no content and is located below the "Download Selected" button */
html body div.container div.middle span#body_span div.tvtbox div.body form#favForm table#favTorrentList
{
    margin-bottom: 4px !important;
    margin-top: 4px !important;
}

/* Fav table row background (normal) */
html body div.container div.middle span#body_span div.tvtbox div.body form#favForm table tbody tr 
{
    background-color: #444 !important;
}
/* Fav table row background (hover) */
html body div.container div.middle span#body_span div.tvtbox div.body form#favForm table tbody tr:hover 
{
    background-color: #222 !important;
}







/**** Private Messaging ****/


/*** INBOX ***/


/** Header **/
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr.rub td,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr.rub td
{
   background-color: #222 !important;
   padding: 3px !important;
}


/** Read messages **/
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr.list_even td
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr[style],
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr[style] td:hover,
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr[style] td a,
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr[style] td,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr[style],
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr[style] td:hover,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr[style] td a,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr[style] td
{
  background-color: #555 !important;
  font-weight: normal !important;
  vertical-align: middle !important;
}
/* From/Subjects left padding */
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr td a,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr td a
{
    padding-left: 7px !important;
}
/* Normal messages: Subject hovered */
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr[style] td a:hover,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr[style] td a:hover
{
    color:#FFF !important;
    text-decoration: underline !important;
}


/** Unread messages **/
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr.list_even td,
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr.list_even td:hover,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr.list_even td,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr.list_even td:hover
{
  background-color: #666 !important;
  font-weight: bold !important;
}
/* Subject hovered */
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr.list_even td a:hover,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr.list_even td a:hover
{
    color:#FFF !important;
    text-decoration: underline !important;
}
/* Date column */
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr.list_even td.datetime,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr.list_even td.datetime
{
    font-weight: normal !important;
  /*padding-left: 5px !important;
    padding-right: 5px !important;*/
}


/* Footer */
html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr td,
html body div.container div.middle span#body_span div#msgs.sidebox div.boxbody form table tbody tr td
{
    background-color: #222 !important;
}
/*html body div.container div.middle span#body_span div#msgs div.tvtbox div.body form table tbody tr td[align="left"]
{
    padding-left: 5px  !important;
]*/


/* Reply Message input filed */

html body div.container div.middle span#body_span div#msgs div.tvtbox div.body div#reply form textarea#reply_text.tinput
{
    color: #EEE !important;
    background-color: #111 !important;
}




}
