css:
@-moz-document url-prefix("http://feedly.com/"), url-prefix("https://feedly.com/") {
BODY.home
{
    background-color: #111 !important;
  	
}

#feedlyProBar { display: none !important; }
#proBanner { display: none !important; }

#feedlyTabs
{
	background-color: #111 !important;
  	color:#DDD !important;
}

#feedlyTabsHolder
{
	background-color: #151515 !important;
}

.narrow .dark #feedlyTabsHolder .nonEmpty
{
	color:#CCC !important;
}

.narrow .dark #feedlyTabsHolder .Empty
{
	color:#CCC !important;
}

#floatingBar
{
  	background-color: #111 !important;
}

#floatingBar h1
{
  	color: #ECECEC !important;
}

#feedlyPart0.area
{
    background-color: #111 !important;
 	color: #BBB !important;
}

#feedlyTitleBar
{
	color: #ECECEC !important;
}

.pageActionBar
{
    background-color: #555 !important;
}

.entryHolder
{
    background-color: #111 !important;
}

.inlineFrame
{
    background-color: #111 !important;
   	color: #BBB !important;
}

.entryBody ,h1 ,h2
{
    color: #BBB !important;
}

.u0Entry:hover
{
    background-color: #222 !important;
}

.u0Entry
{
	background-color: #111 !important;
	border: 1px solid #111 !important;
}

.selectedEntry
{
	background-color: #062A00 !important;
  	color: #FFF !important;
}

.selectedEntry:hover
{
      background-color: #173B11 !important;
}

.unread
{
    color: #AAA !important;
}

.inlineFrame .u100entry .title
{
	color: #ECECEC !important;
}

.websiteCallForAction
{
	background-color: #222!important;
	color: #BBB !important;
}

.websiteCallForAction:hover
{
	background-color: #333 !important;
	color: #BBB !important;
}

.topWikiWidget
{
	background-color: #444 !important;
  	padding-bottom: 0px !important;
}

.bottomWikiWidget
{
	background-color: #444 !important;
}

.viewerIcon {
	color: #999 !important;
	background-color: #444 !important;
}

.toBeTagged
{
	color: #000 !important;
	background-color: #444 !important;
}

.wikiWidgetShare
{
    background-color: #444 !important;
  	background-image: none !important;
}

#aboutArea
{
	border-top-color: #111 !important;
  	background-color: #111 !important;
}

#bigMarkAllAsReadButton
{
  	background-color: #010A00 !important;
}

.secondaryCallForAction
{
	color: #333;
}

.entryBody .summary b, .entryBody .content b
{
	color: #BBB !important;
}

.entryBody a
{
    color: #505090 !important;
}
    
.entryBody a:visited
{
    color: #703070 !important;
}
}
