css:
#menu-section-mi a, .navbar{
background: rgb(0,53,107); /* Old browsers */
background: -moz-linear-gradient(top,  rgba(0,53,107,1) 0%, rgba(15,180,231,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,53,107,1)), color-stop(100%,rgba(15,180,231,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%); /* IE10+ */
background: linear-gradient(to bottom,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%); /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00356b', endColorstr='#0fb4e7',GradientType=0 ); /* IE6-9 */
}

#menu-section-mi a{
background: rgb(0,53,107); /* Old browsers */
background: -moz-linear-gradient(top,  rgba(0,53,107,1) 0%, rgba(15,180,231,1) 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,53,107,1)), color-stop(100%,rgba(15,180,231,1))) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  rgba(0,53,107,1) 0%,rgba(15,180,231,1) 100%) !important; /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00356b', endColorstr='#0fb4e7',GradientType=0 ) !important; /* IE6-9 */
}

#menu-section-mi a:hover
{

background-image: -webkit-linear-gradient(bottom, rgba(0,126,255,.74), rgba(0,255,234,.74)) !important;
	background-image: -moz-linear-gradient(bottom, rgba(0,126,255,.74), rgba(0,255,234,.74)) !important;
	background-image: -o-linear-gradient(bottom, rgba(0,126,255,.74), rgba(0,255,234,.74)) !important;
	background-image: -ms-linear-gradient(bottom, rgba(0,126,255,.74), rgba(0,255,234,.74)) !important;
	background-image: linear-gradient(to top, rgba(0,126,255,.74), rgba(0,255,234,.74)) !important;
}

#menu-section-mi a
{


z-index: 2 !important;
  
right: 1000px !important;
  
top: 13px !important;


-webkit-border-radius: 6px !important;
-moz-border-radius: 6px !important;
border-radius: 6px !important;
  

   border: none !important;
}


#menu-section-mi a, .navbar{
  
-webkit-box-shadow: 0px 0px 8px rgba(0, 82, 96, 1);
-moz-box-shadow:    0px 0px 8px rgba(0, 82, 96, 1);
box-shadow:         0px 0px 8px rgba(0, 82, 96, 1);

position:fixed !important;

}

.navbar {



  
 right: 700px !important;
  
}


.navbar:hover, #menu-section-mi a:hover{


  
-webkit-box-shadow: 0px 0px 8px  rgba(0, 202, 255, 1);
-moz-box-shadow:    0px 0px 8px  rgba(0, 202, 255, 1);
  box-shadow:         0px 0px 8px  rgba(0, 202, 255, 1);
      
    }
      
