css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbs.themex.net") {
body {
	background:#FAFAFA !important;
}
a,a:visited, body_avisited,a:link, body_alink{
  	color:#666 !important;
}
a:hover{
  	color:#ff4c4b !important;
}
#shad-t-r,#shad-t-l,#shad-t {
	display:none !important;
}
#shad-th-r {
	padding:0px !important;
	background:none !important;
}
#shad-th-l {
	padding:0px !important;
	min-height:40px !important;
	background:none !important;
}
#header-tb-r,#header-tb-l {
	display:none !important;
}
#header-tb {
	margin:0px !important;
	height:40px !important;
	background:#33373b !important;
}
#widget-r,#widget-l {
	display:none !important;
}
#tb-caption {
	display:none !important;
}
#tb-nav {
	margin:0 auto !important;
	width:1200px !important;
	height:40px !important;
}
#tb-nav #tb-icons li {
	height:40px !important;
	line-height:40px !important;
}
#tb-nav #tb-icons li a {
	padding:0px 10px !important;
	color:#FFF !important;
	line-height:40px !important;
}
#tb-nav #tb-icons li a:hover {
	background:#202226 !important;
	color:#ff4c4b !important;
}
#tb-nav #tb-icons li a img {
	display:none !important;
}
#tb-nav #tb-icons li a br {
	display:none !important;
}
#tb-nav #tb-icons li#tb-logo {
	display:none !important;
}
#tb-searchdiv {
	padding:0px !important;
	width:280px !important;
}
#tb-searchbox {
	float:left !important;
	padding:0px !important;
	width:194px !important;
	height:40px !important;
	background:none !important;
}
#tb-searchbox input {
	float:left !important;
	margin-top:5px !important;
	padding:4px 7px !important;
	width:180px !important;
	height:21px !important;
	outline:0 !important;
	border:none !important;
	border-radius:2px 0px 0px 2px !important;
	color:#777 !important;
}
#tb-searchdiv a {
	float:left !important;
	margin-top:5px !important;
	padding:0 10px !important;
	height:29px !important;
	min-width:36px !important;
	border:none !important;
	border-radius:0 2px 2px 0 !important;
	background:#ff4c4b !important;
	color:#FFF !important;
	text-align:center !important;
	font-weight:normal !important;
	line-height:29px !important;
	cursor:pointer !important;
	_overflow:hidden !important;
}
#shad-r,#shad-l {
	padding:0px !important;
	background:none !important;
}
#page-body {
	border-top:none !important;
  	background:#FAFAFA !important;
  	padding-bottom: 20px !important;
}
#breadcrumb {
	padding:10px 0px !important;
	border-bottom:1px solid #DEDEDE !important;
	background:#FBFBFB !important;
}
#breadcrumb table {
	margin:0px auto !important;
	width:1200px !important;
}
#page-body>table {
	margin:0px auto !important;
	width:1200px !important;
	border:1px solid #DEDEDE !important;
	border-top:none !important;
  	border-bottom:none !important;
}
.aquag {
	display:none !important;
	background:none !important;
}
.bleft {
	border-left:1px solid #DEDEDE !important;
}
.tcat {
	border-bottom:1px solid #DEDEDE !important;
	background:url(data:image/png;base64,) 0px -20px repeat-x !important;
	color:#444 !important;
	text-shadow:none !important;
	line-height:25px !important;
}
.tcat a {
	color:#444 !important;
	font-weight:bold !important;
	font-size:12px !important;
	line-height:25px !important;
}
.tcat a:first-child {
	margin-top:5px !important;
	padding-left:2px !important;
	background:#33373b !important;
}
#shad-b-r,#shad-b-l,#shad-b {
	display:none !important;
}
#page-body table > tbody[id*=collapseobj_forumbit] {
}
#page-body table > tbody[id*=collapseobj_forumbit]>tr>td {
	border-bottom:1px solid #EAEAEA !important;
}
#page-body table > tbody[id*=collapseobj_forumbit]>tr>td[valign*=top] {
        display:none !important;
}
#page-body table > tbody[id*=collapseobj_forumhome] {
	display:none !important;
}
#page-body table > tbody:nth-child(n+20) {
	display:none !important;
}
#page-body > .pad4sides {
	display:none !important;
}
.alt2 {
	background:#FAFAFA !important;
}
#page-body > form{
	width:1200px !important;
	margin:0px auto !important;
	background: #FBFBFB !important;
	color:#999 !important;
}
#page-body > form >.pad4sides{
  	border:1px solid #DEDEDE !important;
  	border-top:none !important;
}
#fb-nav{
  	display:none !important;
}
#breadcrumb_self,#breadcrumb_start{
  	background:none !important;
  	padding:1px 0 4px 0px !important;
}
#breadcrumb_end{
  	background:none !important;
  	padding:1px 0 4px 20px !important;
}
a[href*="rss"]{
  	display:none !important;
}
.alt2 td[align*="left"] div.smallfont,.alt1 td[align*="left"] div.smallfont{
  	display:none !important;
}
.alt2 td[align*="left"] span.smallfont,.alt1 td[align*="left"] span.smallfont{
	display:none !important;
}
.alt2 td[align*="left"] > div > a ,.alt1 td[align*="left"] > div > a{
	color:#777 !important;
}
.alt2 td[align*="left"] > div > a:hover ,.alt1 td[align*="left"] > div > a:hover{
	color:#ff4c4b !important;
}
.pad4all{
  	border:1px solid #DEDEDE !important;
  	background:#FCFCFC !important;
  	margin-top: -1px !important;
}
.bbot{
  	border:1px solid #DEDEDE !important;
  	border-top:none !important;
  	border-bottom:none !important;
}
.pad8all.btop,.btop.pad4top,.btop.pad4all{
  	display:none !important;
}
div[align*="center"].btop{
   	display:none !important;
}
table.bbot tr td[valign*="top"]{
  display:none !important;
}
#page-body > div.pad4all{
  	width:1198px !important;
  	margin:0px auto !important;
  	padding:0px !important;
  	border-top:none !important;
}
#page-body > div.pad4all table{
  	padding:4px !important;
}

#posts{
  	width:1198px !important;
  	margin:0px auto !important;
  	border: 1px solid #DEDEDE !important;
	border-top: none !important;
	border-bottom: none !important;
}
.sig{
    display:none !important;
}
#posts .alt1{
 	padding:0px  !important;
}
.bbot2{
  	padding:6px  !important;
  	border-color:#DEDEDE !important;
    background:#FBFBFB !important;
}
.post{
    padding:10px !important;
}
.btop{
  	border-color:#DEDEDE !important;
}
font[color*="blue"]{
    color: #ff4c4b !important;
}
#posts table[id*=post]{
	position: relative;
}
#posts table tbody tr[valign*=top] td.postbit{
  	background:#FFF !important;
    padding:0px !important;
    border-color:#DEDEDE !important;
    position: relative;
}

#posts table tbody tr[valign*=top] td.postbit > div:last-child
{
	position: absolute;
	top: 29px;
	left: 175px;
	background:#FFF;
	width: 160px;
	border: 1px solid #DEDEDE;
	display: none;
}
#posts table tbody tr[valign*=top] td.postbit > div:first-child{
  	border-bottom: 1px solid #DEDEDE !important;
	height: 29px !important;
	padding: 0px  !important;
	line-height: 29px !important;
  	background:#FBFBFB !important;
}
#posts table tbody tr[valign*=top] td.postbit > div.avatar{
    padding:17px 0px !important;
  }
#posts table tbody tr[valign*=top] td.postbit:hover > div:last-child{
       display:block;
}
#posts table tbody tr td.postbit{
	background: #FFF ! important;
}
.postsep.btop.bbot{
	width: 1196px !important;
	padding: 0px !important;
	height: 1px !important;
	background: #DEDEDE !important;
}
.postsep.btop.bbot img{
 	display:none !important;
}
div[id*="post_thanks_box"] table tr td > div:last-child{
	padding:10px  !important;
}
#page-body > form[name="vbform"]{
  	width:1198px !important;
  	border:1px solid #DEDEDE !important;
  	margin:-1px auto 0px auto  !important;
  	background:#FFF !important;
}
a.bnewthread,a.bnewreply{
	display: inline-block !important;
	border: 1px solid #CCC !important;
	border-radius: 2px !important;
	font-weight: bold !important;
	background-repeat: no-repeat !important;
	background: #F7F7F7 url(data:image/png;base64,) no-repeat  -47px -119px !important;
	box-shadow: inset 0 1px 0 white !important;
	color: #666 !important;
	width:110px !important;
	height:auto !important;
}
a.bnewreply{
  	width:75px !important;
}
a.bnewthread span,a.bnewreply span{
  	padding: 5px 10px !important;
	margin-left: 25px !important;
	display: block !important;
	background: #F7F7F7 !important;
	box-shadow: inset 0 1px 0 white !important;
  	font-size:13px !important;
}
.tborder{
	background: #F7F7F7 !important;
	color: #666 !important;
	border: none !important;
	border-radius: 2px !important;
}
.tborder tr td{
	border:1px solid #DEDEDE !important;
	border-right:none !important;
	padding:6px 10px !important;
}
.tborder tr td:last-child{
	border-right:1px solid #DEDEDE !important;
}
.tborder tr td.vbmenu_control{
	background:#ff4c4b !important;
	border:none !important;
	border-radius:2px 0px 0px 2px !important;
}
.alt3 {
	background: #ffe1e1 !important;
	width: 1198px !important;
	margin: 0px auto !important;
	border: 1px solid #DEDEDE !important;
	padding: 6px 0px  !important;
	margin-top: -1px !important;
}
.dropdown{
  	border-color:#DEDEDE !important;
    background:#FFF !important;
    border-radius:3px !important;
}
.dropdown dl{
  padding:0px !important;
}
.dropdown dl dt{
  	border-bottom: 1px solid #DEDEDE !important;
	background: url(data:image/png;base64,) 0px -20px repeat-x !important;
	color: #444 !important;
	text-shadow: none !important;
    border-radius:3px 3px 0px 0px !important;
}
.dropdown dd a:hover{
  	background: #EFEFEF !important;
  	color: #545454 !important;
}
#threadsearch{
  	display:none !important;
}
#posts .alt1>div.btop2{
	height: 30px !important;
	line-height: 30px;
	padding-top: 0px !important;
    overflow: hidden !important;
}
#posts .alt1>div.btop2 > table tr td[align*=right]{
	background: -moz-linear-gradient(top,  #fdfdfd 0%, #F7F7F7 100%); 
	background:-webkit-gradient(linear, 0 0, 0 bottom, from(#fdfdfd), to(#F7F7F7));
	height: 30px !important;
	line-height: 30px;
	overflow: hidden !important;
}
#posts .alt1>div.btop2>table tr td>a{
	display: inline-block;
	height: 30px;
	line-height: 30px;
	border-top: 0px;
	border-bottom: 0px;
	margin-top: 0px;
	font-size: 0px !important;
	padding: 0px !important;
}
#posts .alt1>div.btop2>table tr td>a img{
  width:0px;
  height:0px;
  background: url(data:image/png;base64,) no-repeat 0px 0px;
  padding:15px 0px;
  display: block;
  float: left;
  -moz-transition: all 0.2s linear;
}
#posts .alt1>div.btop2>table tr td>a img[src*=quote]{
  background-position:0px 0px;
  padding:15px 22px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=multiquote]{
  background-position:-44px 0px;
  padding:15px 35px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=multiquote_on]{
  background-position:-44px -30px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=quickreply]{
  background-position:-113px 0px;
  padding:15px 35px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=thanks]{
  background-position:-183px 0px;
  padding:15px 22px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=edit]{
  background-position:-226px 0px;
  padding:15px 23px;
}
#posts .alt1>div.btop2>table tr td>a:hover img[src*=quote]{
  background-position:0px -30px;
}
#posts .alt1>div.btop2>table tr td>a:hover img[src*=multiquote]{
  background-position:-44px -30px;
}
#posts .alt1>div.btop2>table tr td>a:hover img[src*=multiquote_on]{
  background-position:-44px -30px;
}
#posts .alt1>div.btop2>table tr td>a:hover img[src*=quickreply]{
  background-position:-113px -30px;
}
#posts .alt1>div.btop2>table tr td>a:hover img[src*=thanks]{
  background-position:-183px -30px;
}
#posts .alt1>div.btop2>table tr td>a:hover img[src*=edit]{
  background-position:-226px -30px;
}
#posts .alt1>div.btop2>table tr td>a:hover img{
  -moz-transition: all 0.2s linear;
}
#threadslist .alt3{
  display:none;
}
}
