css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("joindiaspora.com"),domain("diaspora.eigenlab.org"),domain("diasp.org"),domain("ottospora.nl") {
body {
    background: #262729 !important;
    color: #fff !important;
}
h1,h2,h3,h4,h5,h6 {
    color: #fff !important;
}
p {
    color: #fff !important;
}
li {
    color: #acacac !important;
}
/* beginning of what I added */
.stream_element {
    border: 1px #444 solid !important;
    box-shadow: 2px 2px 6px #1A1A1A !important;
    margin-top:8px !important;
    background:#151619 !important;
}
.stream .stream_element:first-child {
          border-top: 1px #444 solid !important;
}
ul.left_nav a.home_selector:hover, .tag:hover {
          background: #454649 !important;
    border-radius: 3px !important;
    text-decoration:none !important;
    color:#eeeeef !important;
}
.tag {
    background: #454649 !important;
    color:#dadbdd !important;
    border-radius: 3px !important;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.45) !important;
    padding: 0px 6px 0px 6px !important;
    margin: 2px 0 2px 0 !important;
}
.tag:hover {background: #555659 !important; color: #fafafc;}
/* end of what I added */
}
