css:
@-moz-document url-prefix("https://digg.com/reader") {
div {
direction: rtl;
}
.dr-root-feed-list>.dr-feed-list-item .dr-feed-label {
padding-right: 50px;
padding-left: auto;
}
.dr-label {
padding: .423em 48px .500em 0px;
}
.dr-root-feed-list>.dr-feed-list-item .dr-feed-badge {
right: 18px;
}
.dr-folder-toggle {
right: 12px;
}
.dr-feed-list-folder .dr-feed-label {
padding-right: 82px;
}
.dr-feed-badge {
right: 50px;
}
.dr-persistent-nav .dr-label {
background-position: 97% -2px;
}
.feed-stream-header {
padding: 1.5em 20px;
}
.modal .modal-header .btn-modal-close {
left: 12px;
right:auto
}
.view-region-nav {
padding-right: auto;
padding-left: 0px;
}
.col-right {
margin-right: 251px;
margin-left: auto;
}
.col-left {
float: right;
direction: ltr;

}
.view-region-main-container{
direction: ltr;
}
.feeditem-list .feeditem-feedtitle {
float: right;
padding: 1.2em 20px 1em .5em;
}
.feeditem-list .story-actions-container {
left: 0;
right:auto;
margin-right: 0;
margin-right: auto;
text-align: left;
}
.feeditem-list .story-data {
margin-left: 259px;
margin-right: auto;
}
.story-actions {
left: 1em;
}
.feeditem-list .feeditem-fuzzytime {
margin-left: 135px;
margin-right: 62px;
right: 1em;
}
.site-header-logo-container {
margin: 0;
float: right;
}
.reader-nav {
left: 0;
right:auto
}
.reader-nav .reader-nav-settings .reader-nav-tooltip {
margin: 4.7em -9em 0 0;
}
.reader-nav .reader-nav-text-item {
padding: 18px;
margin-left: 16px;
}
.site-header-nav .nav-item {
margin: 0;
font-size: .875em;
float: right;
}
.site-header-nav .nav-list {
direction: ltr;
}
.reader-nav .reader-nav-settings .reader-nav-tooltip:before {
right: 135px;
}
.reader-nav .reader-nav-settings .reader-nav-tooltip:after {
right: 136px;
}
#view-region-view-type-selector .view-types {
margin: 4.7em -6.25em 0 0;
}
#view-region-view-type-selector .view-types:before {
right: 100px;
}
#view-region-view-type-selector .view-types:after {
right: 101px;
}
.tooltip-subscription-add:before {
right: 15px;
}
.tooltip-subscription-add:after {
right: 16px;
}
}
