css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("u-cursos.cl") {
/* Barra izquierda */

div#izquierda {text-decoration: none !important;}
div#izquierda div h1 {background-color: #ddf0dd !important;border-right: 1px solid #aaccaa !important;text-decoration: none !important;border-bottom: 1px solid #aaccaa !important;}
div#izquierda div h1 a {color: #006666 !important;text-decoration: none !important;font-weight: bold !important;}
div#izquierda div ul li {text-decoration: none !important;border-style: none solid solid;background-color: white !important;}
div#izquierda div ul li h1 {text-decoration: none !important;border-style: none solid solid;background-color: white !important;border-right: 0px solid white !important;border-bottom: 0px solid white !important;border-left: 0px solid white !important;}
div#izquierda div ul:hover li:hover {background-color: #DBE7F5 !important;text-decoration: none !important;}
div#izquierda div ul:hover li:hover * {background-color: #DBE7F5 !important;text-decoration: none !important;}
div#izquierda div ul:hover li:hover div.active u1.active * {background-color: #333 !important;text-decoration: none !important;}
div#izquierda div ul li.sel h1 {text-decoration: none !important;border-style: none solid solid;background-color: #F1F4F8 !important;border-right: 0px solid #F1F4F8 !important;border-bottom: 0px solid #F1F4F8 !important;}
div#izquierda div ul li a {background-color: none !important;color: #4A4F88 !important;text-decoration: none !important;}
div#izquierda div ul li a:hover * {background-color: #DBE7F5 !important;text-decoration: none !important;}
div#izquierda div ul li.sel {font-weight: none !important; background-color: #F1F4F8 !important;}
div#izquierda div ul li.sel * {font-weight: none !important; background-color: #F1F4F8 !important;}
div#izquierda div.nuevo a {color: black !important;}
div#izquierda li.sel div.nuevo a {color:#333 !important;}
div#izquierda div.off h1 a {color: #0179a1 !important;text-decoration: none !important;}

/* Banner */

h1#ucursos {background-color: transparent !important;border-bottom: 5px solid #018fbe !important;}
h1.modulo {color: #018fbe !important;border-bottom: 1px solid #018fbe !important;}
h2.ucursos {color: #018fbe !important;border-bottom: 5px solid #018fbe !important;background-position: right center !important;}
span {color: gray !important;}
div.instrucciones{padding: 15px;background: transparent url("https://www.u-cursos.cl/diseno/images/gris.png") scroll repeat top left !important;border: 1px solid #000 !important;max-width: 500px;}
.boton {color: #404668 !important;height: 20px;line-height: 100% !important;}
.boton:hover{color: #f2f5f6 !important;}

/* Cuerpo */

#body{color: black !important;}
ul.modulos li.sel img {border:2px solid #018fbe !important;}
a {text-decoration: underline;}
a {color: #509050 !important;text-decoration: none !important;}
a:visited {color: #0066cc !important; text-decoration: none !important;}
a:hover{color: #0066cc !important; text-decoration: underline !important;}
a:active {color: #007979 !important;text-decoration: underline !important;font-weight: normal;}
a:link {color: #0066cc !important; text-decoration: underline !important;}
h1{}
h2 {text-decoration: none !important;}
div.instrucciones *{text-align: justify !important;}
a.boton {color: black !important;text-decoration: none !important;}

/* Foro */

div.nuevo span.autor a.usuario, div.connuevo h1 a {color: #007979 !important;text-decoration: underline !important;font-weight: bold !important;}
div.msg {text-decoration: none !important;}
div.msg h1 a {color: #007979 !important;font-weight: normal;font-size: 14px;}
div.msg h2 {color: black !important;}
div.msg ul.metadata {color: gray !important;}
div.msg ul.metadata li a {font-weight: normal !important;color: gray !important;text-decoration: underline !important}
div.nuevo .texto, div.responder .texto {border-color: rgb(114, 203, 247) !important;background-color: rgba(223, 239, 247, 0.6) !important;}
span.autor a.star {text-decoration: none !important;}
span.autor a.usuario {color: #007979 !important;text-decoration: underline !important;font-weight: normal;}
span.adhesion a {color:gray !important;text-decoration: none !important;}
span.adhesion a:hover {text-decoration: underline !important;}
span.adhesion table tbody td {border: 0px !important;}
ul.paginas a {color: #007979 !important; text-decoration: none !important;}
ul.paginas li.sel a {color: black !important; text-decoration: none !important;}
ul.modulo li.sel a {background: #018fbe !important;color: white !important;border-top: 0px solid #5C6C8E !important; border-right: 1px #018fbe !important;}
em {color: gray !important;}

ul#servicios_generales li a {color: #1C1DB0 !important;}

/* Tablas */

table, th, td {border: 0px solid #ddf0dd !important;}
table{background: #fff !important;}
th {background: #ddf0dd !important;color: #000080 !important;}

/* Nuevos eventos (Material Docente, Mis canales) */

table tr.nuevo.even {background: #E2F9F9 !important;}
table tr.nuevo.odd {background: #E2F9F9 !important;}

/* Detalle de tablas (color de fondo, bordes y texto) */

table tr.foot.odd td.opciones {background: #f4f4f4 !important;}
table tr.foot.even td.opciones {background: #f4f4f4 !important;}
table td.opciones {background: #D7EBEB !important;}
table td.string h1 a {text-decoration: none !important;}
table td.number span {color: black !important;}
table td.number span.reprueba {color: #e10c12 !important;}
table tbody td {border-bottom: 1px solid #aaccaa !important;border-left: 1px solid #ddf0dd !important;border-right: 1px solid #ddf0dd !important;}
table tfoot td {border-bottom: 1px solid #aaccaa !important;}
table tfoot th {border-bottom: 1px solid #aaccaa !important;}

/* Separador de tablas (subtitulos Material Docente, Notas Parciales) */

tr.separador{background-color: #C8E7E7 !important;color: #000080 !important;}


/* Reloj (Horario) */

div#line.reloj span {color: white !important; background: #018fbe !important;}
div#line.reloj {border-top: 1px solid #018fbe !important;}

/* Módulos superiores y Navbar */

td.verde, td.opciones{background: #f9f4e8 !important;}
td.gris{background: #f4f4f4 !important;}
ol, ul {text-decoration: none !important;}
ul.modulos a {text-decoration: none !important;}
ul.modulo li {border-right: 1px solid #1C1DB0 !important;text-decoration: none !important;}
ul.modulo li a {color: #959595 !important;text-decoration: none !important;}
ul.modulos li a:hover span {text-decoration: none !important;}
ul.modulos li span {text-decoration: none !important;}
ul.modulos li.sel, ul.modulos li.sel:hover {border-color: #1C1DB0 !important;text-decoration: none !important;}
ul.opciones li a {font-weight: normal;color: #1B1EB0 !important;text-decoration: underline !important;}
ul#navbar {text-decoration: none !important;}

/* Blog */

div.post {text-decoration: none !important;}
div.post h1 a {color: #007979 !important;font-weight: bold;font-size: 14px; text-decoration: none !important;}
div.post h1 {color: #007979 !important;font-weight: bold;font-size: 14px; text-decoration: none !important;}
div.post h2 {color: gray !important;}
div.post ul.metadata {color: gray !important;}
div.post ul.metadata li a {font-weight: normal !important;color: gray !important;text-decoration: underline !important}

/* Footer */

div#footer a {color: gray !important;}
div#footer li.social a {text-decoration: none !important;}

/* Inicio de U-Cursos */

div.revista h1 {color: #018fbe !important}
div.revista span {color: #007979 !important}

/* Mis marcas */

div.objeto h1 a.star {text-decoration: none !important;}

/* Afiches */

div.lista-afiches h4.afiche-titulo a.star {text-decoration: none !important;}
}
