css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.motherjones.com") {

#header, #sidebar-right, body.front .panel-2col-bricks .center-wrapper-below .panel-col-last,
#page-footer, #contributor-items, div.pane-block:nth-child(3) > h2:nth-child(1), .sidebar-large-left,
.package-header {
    display: none !important;
    }
.sidebar-right #content {
    width: 990px !important;
    }
body.front .panel-2col-bricks .center-wrapper-above .panel-col-first {
    width: 753px !important;
    }
.center-wrapper-above div.pane-frontpage-stories, .center-wrapper-above div.pane-views-photoessay-block-3,
.front .pane-frontpage-stories-panel-pane-1 .views-field- .field-content, .not-front #content-inner   {
    width: 725px !important;
    }
body.front .panel-2col-bricks .center-wrapper-above .panel-col-last {
    width: 178px !important;
    }
body.front .panel-2col-bricks .center-wrapper-below .panel-col-first {
    width: 100% !important;
    }
.article-lists-list {
    width: 100% !important;
    }
.article-lists-list-inner {
    margin: 0 !important;
    }
body.front .panel-2col-bricks .center-wrapper-above .panel-col-first .inside, body.front .center-wrapper-below .panel-col-first .inside,
.article-lists-list-inner  {
    border: none !important;
    }
    
.view-frontpage-stories h3.title, .section-page-view .views-field-title h3.title  {
    font-size: 1.6em !important;
    }
.view-frontpage-stories .views-field-field-dek-value h4.dek-view, h4.dek-view  {
    font-size: 1.5em !important;
    line-height: 1.2 !important;
    }
#front-page-article-lists .item-list h3.title {
    font-size: 1.8em !important;
    }
a {
    transition-property: background-color , color!important;
	transition-duration: 1.25s, .5s !important;
	transition-timing-function: ease-out !important;
	}
#front-page-article-lists .item-list h3.title a:hover,
.view-frontpage-stories h3.title a:hover, .blog-front .item-list h3.title a:hover,
.section-page-view .views-field-title h3.title a:hover {
    color: #fff !important;
    background: red !important;
    }
#node-body-top a:hover, #node-body-bottom a:hover, #node-body-top a:focus, #node-body-bottom a:focus {
    text-decoration: none !important;
    }
.byline {
    font-size: 1em !important;
    }
.not-front #page p, .not-front #page li  {
    font-size: 1.7em !important;
    line-height: 1.3 !important;
    color: #6e6e6e !important;
    }
.blog-front .item-list h3.title {
    font-size: 1.3em !important;
    line-height: 1.2 !important;
    }
#dsq-content, #navbar li.expanded ul.menu li a {
    font-size: 0.5em !important;
    }
}
