/*
* Persian Twitter user style
* Version : 4.5
* Author : Danial Behzadi (@danialbehzadi)
* Updated : Jan 5, 2018
* Website : https://gnu.rocks
* Description : Twitter clear style suitable for Persian users.
* License : CC BY-SA v4 or higher
* Style page: http://userstyles.org/styles/90295/enhanced-persian-twitter
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {

/* Persian fixes */
body.fa, input,textarea, .tweet-content, .tweet-text, .ProfileTweet-text, .TweetTextSize.TweetTextSize--26px, .pretty-link b , [lang="fa"] {
	font-family: "iranian sans", Tahoma, sans-serif !important;
	font-size: 13px !important;
  line-height: 18px !important;
  font-weight: 400 !important;
	text-decoration: none !important;
  letter-spacing: 0 !important;
}

h1, h2, h3, a, button, div.new-tweets-bar, div.module, input.submit, Strong.fullname-rtl {
	font-family: "Droid Arabic Naskh","B Yekan","iranian sans",tahoma,sans-serif !important;
	text-decoration: none !important;
}

.trends-container .flex-module-inner {
	text-align: left;
    direction: ltr;
}

.twitter-hashtag, .twitter-atreply {
    direction: rtl !important;
}

/* Visual Improve */
div.tweet {
	cursor: default !important;}

.js-tooltip b,
.stream-item-header .translate-button {
    display: none;
}

/* Shadows */
.Gallery-content, #timeline,
.stream-item.open, .global-nav-inner, .permalink {
    box-shadow: 0 0 10px black;
}
    
.module, .WhoToFollow, .flex-module {
    background: whitesmoke !important;
}

/* remove headers */

.content-main, .dashboard, #timeline {
    margin-top: -10px;
}

.home-tweet-box {
    margin-top: 0;
}
    
.timeline-tweet-box {
    border: 0 !important;
}

.edge-design .avatar, .edge-design .Avatar, .edge-design .DashboardProfileCard-avatarImage,
.edge-design .DashboardProfileCard-avatarLink, .edge-design .Gallery.is-tweetless .Gallery-content,
.edge-design .Gallery.is-tweetless .Gallery-media,
.edge-design .MomentCapsuleCover .MomentUserByline-avatar,
.edge-design .MomentCapsuleItemTweet--withText .MomentUserByline-avatar,
.edge-design .MomentCapsuleSummary .MomentUserByline-avatar,
.edge-design .MomentMakerRecommendedTweetsSearch--users .MomentMakerRecommendedTweetsSearch-userContainer .avatar,
.edge-design .ProfileAvatar, .edge-design .ProfileAvatar-image, .edge-design .ProfileAvatar-placeholderImage,
.edge-design .ProfileAvatarEditing, .edge-design .ProfileAvatarEditing-button,
.edge-design .ProfileAvatarEditing-overlay, .edge-design .ProfileCard-avatarImage,
.edge-design .ProfileCard-avatarLink, .edge-design .ProfileCardMini-avatarImage,
.edge-design .ProfileListItem-avatar, .edge-design .ProfileUserList .Avatar,
.edge-design .RelatedUsers-users > .account-summary .avatar
{
    border-radius: 5%;
}

.ProfileHeaderCard-birthdateText, .ProfileHeaderCard-joinDateText, .ProfileHeaderCard-locationText, .ProfileHeaderCard-onlineHoursText, .ProfileHeaderCard-responsivenessLevelText, .ProfileHeaderCard-urlText, .ProfileHeaderCard-vineProfileText, .ProfileHeaderCard-periscopeProfileText{
    box-decoration-break:slice;
}

}
