css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("xianguo.com") {

}

@-moz-document url-prefix("http://xianguo.com/reader") {



.item_header{ background:#f7f7f7;padding:8px 6px;border-bottom: 0px solid #eee; margin-bottom:0px; width:auto;}
.item_header:hover{ background:#eee;}
.item_title_mode{ padding:0;border:0;}
.item{ padding:0; border:0px; background:#fff}
.item_header .title a{ font-size:14px; color:#444}
.item_font_13 .item_descr{border-top:1px solid #ddd; margin-top:0; padding-top:20px; padding-bottom:20px;}
.item_selected{ border:1px solid #888;border-radius:0px; overflow:hidden;}
.itemPannel_hr{ background:#d8d8d8}
.item_bottom{border-top: dashed 1px #DDD;background-color: #fbfbfb;}
.item_bottom .icon{ color:#999;}
.item_header .feed{display:none;}

.item_readed .item_header{background: #fdfdfd;}
.item_readed .item_header:hover{ background:#f7f7f7;}


.itemList_header{height:auto}
.itemList_header_function{ padding:8px 5px; height:auto;}
.itemList_header_function span{border:1px solid #dcdcdc; padding:5px 9px; background:#f0f0f0; color:#434343;border-radius:2px; font-weight:bold;}
.itemList_header_function span:hover{ border:1px solid #aaa;}
.itemList_header_function .separator{ border:0; background:0;color:#ccc; padding:5px 4px;}
.itemList_header_function .separator:hover{border:0;}
#unreadSelection,#markPageReaded{border:0; background:0;color:#ccc;}
.itemList_header_function .more_option{ padding:5px; background:#f0f0f0}
.itemList_header_function .option_button{font-weight:normal;}
.itemList_header_function .option_button_selected{}

.feedList_notReaded ,.notRead_Folder{ font-weight:normal; color:#444;}
.feed_unread_li,.folder_ul_unread{ font-weight:normal; color:#777;}
.Sublist{padding:10px 0px 10px 10px;width:225px}
.feed_title_li{max-width:130px}

}
