css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {

/* Style by Wayne R. (AKA Lerkero), last update 2013-November-08 */

body {background: sandybrown !important} /* changes main background color */

.splitter-pane {background-color: #f7be81 !important; color: black !important} /* changes background color of left pane leftover areas */

div#user_info_div {background-color: rgb(44, 44, 44) !important} /* changes the color of the top pane to match the other part. Otherwise it will match the ‘div’ color */

/* below: changing the backgound colors of the various article states*/
.ar.article {background: tan !important}
.ar.article_unreaded {background: #f7be81 !important}
.ar.radius.article_current {background-color: #C88141 !important} /* changes the color of the currently selected article. Color used is "camel brown" */
.ar.article_unreaded.article_current.article_current_collapsed {background-color: #C88141 !important} /* changes the color of the currently selected article. Color used is "camel brown" */

.ar.radius.article.article_current {background-color: #C88141 !important} /*changes the color of the outline of the current article being read. Color used is "camel brown" */
.ar.ar_showed.article.article_current.article_expanded {background-color: #C88141 !important} /*changes the color of the outline of the current article being read. Color used is "camel brown" */

.ar.radius.article {background-color: tan !important} /* changes the color of the box around articles that have already been read but are not currently selected */

.ar.article.article_current.article_current_collapsed {background-color: #C88141 !important} /* changes the color of the box around articles that have already been read but are currently selected */

.ar.article.ar_showed {background-color: tan !important} /* changes the color of the box around articles that have already been read but are not currently selected */

.article_full_contents {background-color: #f7be81 !important} /* changes the color of the background of the currently open article */

td {background-color: #f7be81 !important} /* changes color around links at bottom of open article content */

.footer_inner {background: rgb(44, 44, 44) !important} /* changes the color of the footer pane at the bottom of the page */

/* changing of font colors below */

/* to change the spacing and font color between headlines listed in the feed */
.article_header_text{
        color: black !important;

        padding-top: 2px !important;

        padding-bottom:2px !important;

        vertical-align:bottom;
}

.article_short_contents {color: darkred !important}/* changes the color of preview text next to article headline */

.tree_unreaded {color: darkcyan !important} /* changes text color of unreaded feeds and folders. Add !important to the unread feeds and folders codes below if you want the colors to be different */
.tree_node.tree.normal {color: black !important} /* color of normal folder text */
.tree_node.normal.tree_unreaded {color: darkred !important} /*color of unread folder text */
.tree_sub_node.normal {color: black !important} /*color of normal feed text */
.tree_sub_node.normal.tree_unreaded {color: darkred !important} /* color of unread feed text */
.selected {color: darkcyan !important} /* changes the text color of the currently selected folder or feed */
.header_date {color:black !important} /*changes text color of date in article headline */


.bluelink {color: darkred !important} /* changes text color of ALL links more specificity required for individual links*/
.underlink.bluelink {color: darkred !important} /*changes color or links in articles */
.article_full_contents {color: black !important} /* changes text color within articles and article footer text*/
 span {color: black !important} /* changes text color of unread count and article author */
a.white_link {color: white !important} /* color of links in top toolbar */
span#user_info_realname {color: sandybrown !important} /* color of username in toolbar */

.article_footer {background-color: #C88141 !important}/*changes color of area below open articles */

.parent_div_inner:hover {background-color: sandybrown !important} /* changes highlight color of feed items when mouse cursor moves over them */

/* to change the color of a headline as the mouse cursor moves over it */

.article_header_text:hover{

        background-color: sandybrown !important;

               outline: 1px dotted silver;
}

.ar:hover {background: sandybrown !important} /* to get the whole news item to hightlight */
.ar.article:hover {background-color: sandybrown !important} /* same as highlight above, but for read articles specifically situations */
.ar.article_unreaded:hover {background-color: sandybrown !important} /* same as above but for unread articles */
.ar.article_unreaded.article_current.article_current_collapsed:hover {background-color: sandybrown !important} /* hovering over currently selected article */
.ar.ar_showed.article.article_current.article_expanded:hover {background-color: sandybrown !important} /* hovering over currently expanded article */
.ar.article.article_current.article_current_collapsed:hover {background-color: sandybrown !important} /* hovering over selected, but collapsed article */

.splitter-bar {background: rgb(44, 44, 44) !important} /* change color of splitter bar in middleof panes */

/* Changing the display of the InoReader logo. First line drops blue logo, 2nd line keeps subscription images, 3rd lines adds new logo or text */

div#subscription_options_tick {background: #f7be81 !important} /* changes background color or gear image next to subscriptions text */

div#subscriptions_buttons {background: sandybrown !important} /* changes the color of the header bar which has all the navigation buttons */

DIV#pdinner_0_249079.parent_div_inner.parent_div_inner_selected {background: default !important} /*change the color of the highlight for the selected subscription */

input#feed_searcher {background: default !important; color: default !important} /* changes color of search bar background and font */

}
