css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {

/* Feedly CSS edit by Wayne R (AKA: Lerkero) */

.area {background-color: #f7be81 !important} /* main article area background */
#feedlytabs {background-color: tan !important} /* color of left sidebar */
#navselectorholder {background-color: tan !important; border-style: solid !important; border-color: darkred !important; border-width: 1px !important; border-left-width: 0px !important} /* top-left sidebar color */
#floatingbar {background-color: tan !important} /* top bar color */
#feedlytitlebar {background-color: tan !important} /* Selected feed title color in right panel */
#feedlycenter {background-color: saddlebrown !important} /* color of sidebars on the extreme sides of the reading screen. In all views except list view */

.icon {background-color: tan !important} /* color behind icons in the left sidebar. Should match the color of #feedlytabs depending on what theme is activated for Feedly */

span {color: black !important} /* text color or feed desciption and article summaries */
.infobox {color: darkred !important} /* test color of read feed box info text */
.u0Entry:hover {background-color: sandybrown !important} /*hovering over article titles */
.u0Entry.selectedEntry {background-color: tan !important} /* color of currently selected article */


.inlineframe {background-color: tan !important} /*area around open article */
.entryHolder {background-color: moccasin !important} /* area inside open article */

.sourceTitle {color: darkred !important} /* feed title in articles */

.title {color: black !important} /* text color of article headlines */
.u0summary {color: darkred !important} /* text color of article summaries */

a {color: darkred !important} /* color of Feed titles on the right and links in articles */

.feedIndexTitleHolder:hover {background-color: sandybrown !important} /* hovering over feeds in the left sidebar */
.label:hover {background-color: sandybrown !important} /* hovering over folder in left sidebar */
.label {color: darkred !important}
.label:active {color: darkcyan !important} /* trying to figure out how to get the active folder or feed to be highlighted. Currently not successful */

/* text for unread count for folders */
.categoryUnreadCount {color: darkred !important; 
                        font-weight: bold !important; 
                        font-size: 12px !important;
                        position: relative !important; size: -5px !important;
                        width: 25px !important;
                        height: 20px !important;
                        border-style: solid !important;
                        border-width: 1px !important;
                        border-color: darkred !important
                        }
                        
.feedUnreadCount {color: black !important} /* tect color for number representing unread count */

h2 {color: black !important} /* color of category text in the "Today" view */

.metadata {color: darkred !important} /* color of text that appears in descriptions for magazine view and text that tells subscriber count */

.moreHandle {color: black !important} /* text color for test under feed folders "# more sources" */
.moreHandle:hover {background-color: sandybrown !important} /* hovering over "more source" text */


}
