css:
@namespace svg url(http://www.w3.org/2000/svg);
@-moz-document url-prefix('https://www.tumblr.com/')
{

DIV.l-header-container {
  background: url('https://41.media.tumblr.com/8ca9e9eeab1af12a7b83fa906de10ca5/tumblr_n8wag8ax4b1rxghfko2_1280.jpg') top right fixed;
  background-size: cover;
}
svg|svg.compose-button {fill: rgba(255,255,255,.5)}
svg|svg.compose-button:hover {fill: rgba(255,255,255,1)}

/* blue fix */
div.search_form_row {background-color: rgba(100,100,100,0.2)}
div.l-header .selection_nipple {border-bottom-color: rgba(100,100,100,0.2)}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.blog.has_follow_button:before,
#right_column:after {display: none}

body:not(.mceContentBody) {
  background: url('https://41.media.tumblr.com/8ca9e9eeab1af12a7b83fa906de10ca5/tumblr_n8wag8ax4b1rxghfko2_1280.jpg') #C6E2FF bottom right fixed;
  background-size: cover;
}
.l-content {
  background: rgba(100,100,100,0.2) !important;
}

#dashboard_edit_post {
  background-color: #C6E2FF !important;
}

#right_column .dashboard_nav_item {
  border-bottom: 0px solid #eee !important;
  background-image: none  !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title,
#right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
  color: #fff !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
  color: #888 !important;
}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
  background: none !important;
}

#left_column #posts .notification .avatar {
  border-color: transparent !important;
  background-color: #fbfbfb !important;
  opacity: 0.5 !important;
}

#left_column #posts .notification .avatar:hover {
  opacity: 1 !important;
}

.dashboard_switch_blog_menu_header {
  background-color: #fbfbfb !important;
  color: #666 !important;
}

.dashboard_switch_blog_menu_item {
  background-color: #fbfbfb !important;
  color: #666 !important;
  border: 0px !important;
}

.dashboard_switch_blog_menu_footer {
  background-color: #fbfbfb !important;
  color: #666 !important;
}

.dashboard_switch_blog_menu_wrapper {
  background-color: #fbfbfb !important;
}

#new_post_notice_container {
  color: #fff !important;
}

#new_post_notice_container a {
  color: #fff !important;
}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current {
  background-image: none !important;
}

.new_post_label/*_icon*/ > I {display: none !important}

.new_post_label {
  font-size: 24px !important;
  padding: 6px 1px 73px 0 !important;
  font-family: Helvetica, Trebuchet MS, sans-serif !important;
  text-transform: lowercase !important;
  font-style: italic!important;
  color: #ffffff !important;
  letter-spacing: -3px !important;
}

DIV.post.new_post_buttons {
  background: #fff center no-repeat url('https://40.media.tumblr.com/4c5e93b146dd1dcb4f611fcaa430f0aa/tumblr_n8wag8ax4b1rxghfko1_1280.jpg');
  background-size: cover;
}

#left_column #posts .notification {
  background-color: #fbfbfb !important;
  color: #666 !important;
  border-bottom-color: #eee !important;
}

#left_column #posts .notification .block {
  color: #333 !important;
}

#left_column #posts .notification.alt {
  background-color: #f7f7f7 !important;
}

#left_column #posts .notification.last_notification,
                    #left_column #posts .notification.single_notification {
  border-bottom-color: #ececec !important;
}

#left_column #posts .notification a {
  color: #565656 !important;
  border-bottom: 0px !important;
}

#left_column #posts .notification blockquote {
  border-left-color: #eee !important;
}

#left_column #posts .notification .avatar {
  border-color: transparent !important;
  background-color: #fbfbfb !important;
  opacity: 0.5 !important;
}

#left_column #posts .notification .avatar:hover {
  opacity: 1 !important;
}

.notification_type_icon {
  display: none !important;
}

}
