css:
@-moz-document url("www.cracked.com") {
body, .mainFrame {
    background-color: #800;
}

p {
    font-family: Georgia;
}

.rightColumn {
    background-image: none;
    background-color: #ccc;
}

.module.timelineModule .leftTimeline {
    background-color: #eee;
}

.mainFrame .mainFrameModule.breadCrumbs {
    width: 980px;
    padding-top: 18px;
    padding-right: 0;
    padding-bottom: 12px;
    padding-left: 10px;
}

div.meta {
    text-shadow: none;
}

h1, h2, h3, h4, .mainFrameModule .leftColumn .module .listEntry .meta .title, .linkstorm_list .body ul li .titleLinkStorm a, .mainFrameModule article.module section .Title {
    font-family: 'times new roman', serif;
    text-shadow: none;
}

header h1 {
    font-style: italic;
    font-size: 28px;
}

.widget-publication-list .publication header h1 {
    font-style: normal;
    font-size: 16px;
}

h3.title {
    font-size: 21px;
}

.linkstorm_list .body ul li .titleLinkStorm a, .mainFrameModule article.module section .Title {
    font-size: 20px;
}

#Comments .Comment .meta .badgeWrap .badge {
    background-repeat: no-repeat;
}

#forums td {
    background-color: #fcfcfc;
}

.widget-publication-list .publication footer .views {
    color: #000;
}

.module.GenericRRModule.flashback .body .meta .views {
    color: #000;
}

.featuredModule .fullCentralFeatured .metaInfo span a, .featuredModule .halfCentralFeatured .metaInfo span a, .featuredModule .smallFeatured .metaInfo span a, .featuredModule .mediumFeatured .metaInfo span a, .featuredModule .halfCentralFeatured .metaInfo span, .featuredModule .smallFeatured .metaInfo span {
    color: #ff0;
}

#Comments .Comment .meta .Vote li.Vote_Positive {
    color: #060;
    font-weight: bold;
}

#Comments .Comment .meta .Vote li.Vote_Negative {
    color: #a00;
    font-weight: bold;
}

}
