@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.drudgereport.com"), domain("drudgereport.com") {


/* latin-ext */
@font-face {
  font-family: 'Limelight';
  font-style: normal;
  font-weight: 400;
  src: local('Limelight'), url(https://fonts.gstatic.com/s/limelight/v8/XLYkIZL7aopJVbZJHDuoNOlHjHUmTUFt.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Limelight';
  font-style: normal;
  font-weight: 400;
  src: local('Limelight'), url(https://fonts.gstatic.com/s/limelight/v8/XLYkIZL7aopJVbZJHDuoOulHjHUmTQ.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* latin */
@font-face {
  font-family: 'Special Elite';
  font-style: normal;
  font-weight: 400;
  src: local('Special Elite Regular'), local('SpecialElite-Regular'), url(https://fonts.gstatic.com/s/specialelite/v8/XLYgIZbkc4JPUL5CVArUVL0ntnAOSFNuQsI.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}


* {font-family: 'Special Elite' !important;
 font-style: normal !important;
  font-weight: 400 !important;
}

 html body tt b font font center a img, body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(2) > a:nth-child(2) {
    visibility: hidden !important;
margin-left: -600px !important;
  }
  html body tt b font font center a::after, body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(2) a::after {
    content: 'Drudge Report';
  font-family: 'Limelight';
font-style: normal;
font-weight: 400;
  font-size: 84px !important;
  }

 #drudgeTopHeadlines > tt:nth-child(5) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1)::after,
  body > tt:nth-child(5) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1)::after,
body > tt:nth-child(5) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1)::after,
  body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1)::after, body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(2) > a:nth-child(1)::after {
  content: 'Drudge Report';
  font-family: 'Limelight';
font-style: normal;
font-weight: 400;
  font-size: 84px !important;
}
body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(4) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1):hover,
body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1):hover, 
body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(2) > a:nth-child(1){
    background: none !important;
    color: #DBD6BE !important;
    border-right: none !important;
}

#drudgeTopHeadlines > tt:nth-child(5) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1) > img:nth-child(1),
body > tt:nth-child(5) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1) > img:nth-child(1),
body > tt:nth-child(5) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1) > img:nth-child(1),
  body > tt:nth-child(1) > b:nth-child(1) > font:nth-child(3) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1) > img:nth-child(1){
  visibility: hidden !important;
margin-left: -600px !important;
}

#app_mainheadline > tt:nth-child(1) > b:nth-child(1) > center:nth-child(1) > font:nth-child(1) > font:nth-child(1) > img:nth-child(1) {
    margin-bottom: 20px !important;
  }
    
body {
    background: #181818 !important;
    color: #FAE042 !important;
    }

body, table {
    font-size: 24px !important;
    line-height: 1.3 !important;
    }
center {
    line-height: 3em !important;
}
a {
  padding-top: 5px !important;
   color: #DBD6BE !important;
    text-decoration: none !important;
    transition-property: background-color, border-right, color!important;
    transition-duration: .35s, .25s !important;
    transition-timing-function: ease-out !important;
    }
 a:hover {
    color: #307498 !important;
    text-decoration: none !important;
    background: #90DB90 !important;
    border-right: 10px solid #FAE042 !important;
    }
    
    #drudgeTopHeadlines > tt:nth-child(1) > b:nth-child(1) > font:nth-child(6) > font:nth-child(1) > center:nth-child(1) > a:nth-child(1) {
        padding-top: 30px !important;
    }

hr {
    color: #00ABC9 !important;
    }
}
