css:
@-moz-document url-prefix("https://www.flickr.com/groups/"), url-prefix("http://www.flickr.com/groups/") {
.hd h3 {
  font-size: 1.2em !important;
  font-weight: bold !important;
  display:inline !important;
  margin: 0 0 0 0 !important;
  white-space: normal !important;
}
  
#DiscussTopic .Who, #DiscussTopic .Said, #DiscussTopic .Said h4, #DiscussTopic .Butt {
   
   font-size: 14px !important;
}
  
blockquote {
  display: block;
  margin-top: 1em;
  margin-left: 40px;
-webkit-margin-before: 1em;
-webkit-margin-start: 40px;
}

em {
  	font-style: italic;
}

.post-links {
    display: inline !important;
}
}

@-moz-document regexp("https?://www.flickr.com/groups/[^/]+/discuss(/?|/page\\d+/?)(\\?.*)?(#.*)?") {
.message p {display:none !important;}
.message {
  min-height: 0px !important;
  margin: 0 0 0 0 !important;  
  }
.message .ft {
  max-width: 500px !important;
  margin: 0 0 0 0 !important;
}
.topic-list {
  white-space: normal !important;  
}

.topic .bd {
  display:none !important;
  }
.topic-list .bd {
  margin: 0 0 0 0 !important;
  }
.poster {
  position: static !important;
  float: right !important;
  display: none !important;
}
.poster img {
  display:none !important;
}
.ft br {
     display: none !important;
 }

.message {
  margin: 0 0 0 0 !important;
}
.topic-list .ft {
  position: static !important;
  display: block !important;
  margin-top: -1em !important;
}
.topic-list .hd{
  display:inline !important;  
  margin: 0 0 0 0 !important;
  float: left !important;
  max-width: 700px !important;
}
.topic-list .bd {
  display:inline-block !important;  
  margin: 15px 0 0 0 !important;
  float: right !important;
 }
.hd h3 {
  vertical-align: bottom !important;
}

.topic {
  margin: 0 0 0 0 !important;
    border-bottom: 0px !important;
    padding-top: 0px !important;
}

.group-topics li {
  display: block !important;
  margin: 0 0 0 0 !important;
    border-bottom: 0px !important;
  border-top: 1px solid #eee  !important;
  padding: 5px 0 5px 0 !important;
}

  
.group-topics-col {
      left: -80px !important;
 	width: 460px !important;
      margin-left: 10px !important;
    margin-right: 0 !important;
}
}

@-moz-document regexp("https?://www.flickr.com/groups/[^/]+(/?|/pool(/.*)?|/discuss/\\d+(/?|/page\\d+/?))(\\?.*)?(#.*)?") {
.message p {display:none !important;}
.message {
  min-height: 0px !important;
    margin-left: 0 !important;
  }

.hd {
  display:inline !important;
  margin: 0 0 0 0 !important;
}

.bd {
  float: right !important;
  display: inline-block !important;
  margin: 0 0 0 0 !important;
}
.poster, .reply img, .reply p:first-child {
  display:none !important;
}
.author p {
  display:inline !important;
}
.author {
  float: right !important;
}
.discuss-topic, .group-meta {
  display: inline !important;
  margin: 0 0 0 0 !important;
  overflow: visible !important;
  text-overflow: none !important; 
}
.discuss-topic {
  margin-top: -10px !important;
  white-space: normal !important;  
}

.topic {
  margin: 0 0 10px 0 !important;
  border-top: 1px solid #eee  !important;
  border-bottom: 0px !important;
  padding-top: 0px !important;
  min-height: 0 !important;
}

.group-topics li {
  display:block !important;
  padding-bottom: 0 !important;
  border-bottom: 0px !important;

}


.group-topics-col {

 	width: 460px !important;
      margin-left: 10px !important;
    margin-right: 5px !important;
  display: block !important;
  float: left !important;
}
.hide {
  margin-right: 20px !important;
}

.groups-controls {
   margin-right: 20px !important;
}

#main {
  margin-left: 0 !important;
  margin-right: 0 !important;
}
#subnav-refresh {
	left: 0 !important;
}
#group-sidebar {
  width: 340px !important;
}

.author {
    float: left !important;
    display: block !important;
    margin-right: 6px !important;
}

.said h4 {
    display: inline-block !important;
}
}
