#modal-bg {
	cursor: pointer !important;
	background: url("http://img9.warez-bb.org/wbb3_theme/images/wbb3/opac-bg.png") repeat;
	background: rgba(255, 255, 255, 0.6) url("http://img9.warez-bb.org/wbb3_theme/images/wbb3/bar-load.gif") center center no-repeat;
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	z-index: 2;	
}

#modal {
	display: none;
	position: fixed;
	top: 55%;
	left: 50%;
	z-index: 30;
	width: 560px;
	margin: -250px 0 0 -280px;
	background-color: #FFF;
	border: 1px #111 solid;
	border: 1px solid rgba(0, 0, 0, 0.3);
	-webkit-box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
	-moz-box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
	box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
	-webkit-background-clip: padding-box;
	-moz-background-clip: padding-box;
	background-clip: padding-box;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
}

#modal .header {
	border-bottom: 1px solid #EEE;
	padding: 10px 15px;	
}

#modal .header h3 {
	font-size: 17px;
	font-weight: normal;
	text-align: left;
	margin: 0;	
}

#modal .header .close {
	color: #000;
	float: right;
	font-size: 20px;
	font-weight: bold;
	line-height: 17px;
	opacity: 0.2;
	text-shadow: 0 1px 0 #FFF;
	font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
}

#modal .header .close:hover {
	cursor: pointer;	
	opacity: 0.4;
}

#modal .content {
	max-height: 400px;
	overflow: hidden;
	overflow-y: auto;
	color: #666;
	word-wrap: break-word;
	padding: 15px;	
}

#modal .footer {
	color: #666;
	padding: 4px 0 5px;
	margin-bottom: 0;
	text-align: right;
	background-color: #F5F5F5;
	border-top: 1px solid #DDD;
	-webkit-border-radius: 0 0 6px 6px;
	-moz-border-radius: 0 0 6px 6px;
	border-radius: 0 0 6px 6px;
	-webkit-box-shadow: inset 0 1px 0 #FFF;
	-moz-box-shadow: inset 0 1px 0 #FFF;
	box-shadow: inset 0 1px 0 #FFF;
}

#modal input[type=text] {
	width: 98%;	
}

#modal .input-row > div {
	width: 23%;	
}

#modal .input-row > .input {
	width: 77%;	
}

#modal .input-seperator {
	border-bottom: 1px #EEE solid;
	border-top: none;	
}

#excluded-words {
	padding-bottom: 2px;	
}

#excluded-words .warning-search {
	padding-bottom: 7px;
	margin-bottom: 7px;
	text-align: center;
	font-size: 10px;
	font-weight: bold;
	border-bottom: 1px #EEE solid;	
}

#excluded-words span {
	display: inline-block;
	margin: 0 3px 3px 0;
	background-color: #C00;
	padding: 2px 5px 5px;
	color: #FFF;	
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	border-radius: 2px;
	text-shadow: 0 1px #000;
	line-height: 1;
}

#search-panel #excluded-words {
	width: 50%;	
}

#search-panel #excluded-words .warning-search {
	border-bottom: none;	
}

}
