css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://theoldreader.com/posts/all") {

    div.navbar-fixed-top { display:none !important }
    .reader .container-fluid { top: 0 !important }
    .static { display: none !important; }
    .slide { top: 0px !important; } 
    div#sidebar { display: none !important; }
    div.subscribe-fixed-top { display: none !important; }
    div.reader { left: 0px !important; width: 96% !important; }
    .row-fluid .span9 { width: 96% !important; }
    .reader .body-fixed-top { bottom: 0 !important; left: 0% !important; width: 96% !important; }
    .well { box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.05) inset !important; }
}
