css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("forums.whirlpool.net.au"),
domain("whirlpool.net.au") {

#innerpage, #innershadow, #menu_forum, #menu_news,
#dataentry, #replyformBlock, #menu_whim,
#menu_industry, #box, #list_isp li, #list_isp span,
#list_plan span, #list_brand span, #list_brand li,
.title, .replyuser, .reply, .reply-archived,
.replytools, .article, .block, .threads,
.reps, .reads, .oldest, .newest, .goend, #toc,
ul#tabs.profile li.picked a, #content div,
#left ul#menu > li.selected
{
background:#111111 !important;
}
.group, .bactive, ul#tabs.box li.picked a,
#dataentry th, #jobs h4
{
background:#3A437B !important;
}
#replylist div.reply div.replymeta div.replyuser div.voteblock span.vote
{
background-color:#3A437B !important;
border:1px solid #3A437B !important;
}
textarea
{
background:#000000 !important;
}
#content ul.pagination li a, input, select,
.textarea
{
color:#FFFFFF !important;
background:#000000 !important;
border:1px #3A437B solid !important;
}
#content ul.pagination li.current a
{
color:#FFFFFF !important;
background:#000000 !important;
border:1px #FFFFFF solid !important;
}
.article, .block, #dataentry, #replyformBlock, #box, #toc,
ul#tabs.box li.picked a
{
border:#3A437B 1px solid !important;
}
ul#tabs.profile li.picked a
{
border-top:#111111 2px solid !important;
}
#replylist div.reply,
#threads table tbody tr td
{
border-top:#3A437B 1px solid !important;
}
#replylist div.reply, #latestjobs .job, #jobs h4,
#forumindex table td.title, #forumindex table td.threads
{
border-bottom:#3A437B 1px solid !important;
}
#content .bodytext p.reference
{
border-bottom:none !important;
}
.editor #head 
{
margin:5px !important;
}
.replytext p span
{
display:inline-block !important;
background-color:#000000 !important;
border:1px solid #3A437B !important;
padding:5px !important;
}
a
{
text-decoration:none !important;
}
a:hover
{
text-decoration:underline !important;
}
a, .external, #menu_forum span, #menu_news span,
.bu_name, .article, .thread, .threads, .title,
.archivedate, .usergroup, .username, .userstats,
.replytext, .bodytext, .buttons, .btop span, p,
p span, .reps, .reads, .oldest, .newest, .goend,
#left ul#menu > li.selected > a, .job, strong, 
em, dd, dt, .publisher, h1, h2, h3, h4, h5, p,
.label, .value, #dataentry th, .title h4, .textarea,
textarea, label, .editor, .subtitle, .small, .date,
#jobs, #jobs span, .none, #content div, #list_isp li, 
#list_isp span, #list_plan span, #list_brand li, 
#list_brand span, #list_model li, #list_model span,
.replytext,
#content .bodytext p.reference a
{
color:#FFFFFF !important;
text-shadow:none !important;
}
}
