css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ck101.com") {
    /* @font-face {
  font-family: 'FontAwesome';
  src: local('FontAwesome'),
       url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2') format('woff2'), 
       url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.woff') format('woff'),
       url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf') format('ttf'),
       url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.eot') format('eot');
}
 */
    :root {
        --black1: #080808;
        --black2: #1b1b1b;
        --black3: #222222;
        --black4: #393a3a;
        --grey1: #555555;
        --grey2: #777777;
        --silver1: #888888;
        --silver2: #c1c1c1;
        --silver3: #aaaaaa;
        --silver4: #e5e5e5;
        --blue1: #5294E2
    }
    /* remove add */
    .videoAdWrapper,
    .a_cn,
    .side_ad,
    .adTopBox,
    [id^='sideAds'],
    .adBox,
    .imgAd *,
    #pgt+[class^='ad'],
    .readingList+[class^='ad'],
    .subListTop {
        display: none;
    }
    /* global */
    body {
        background-color: var(--black2);
        color: var(--silver2);
    }
    a {
        color: var(--silver3);
    }
    /* index page */
    .scbar_text {
        background-color: var(--black4) !important;
        color: var(--silver2)
    }
    #toptb,
    #nav,
    .announcements-con a {
        background-color: var(--black1);
        background-image: none;
    }
    .hd-bgcolor[style],
    .hd-bgimg[style] {
        background-color: var(--black1) !important;
        -webkit-filter: grayscale(100%) !important;
        /* Safari 6.0 - 9.0 */
        filter: grayscale(100%) !important;
    }
    .navbar #nv li a {
        color: var(--silver3);
    }
    #wp {
        background-color: var(--black2);
        background-image: none;
    }
    .ckwriterBg {
        background-color: var(--black2);
        background-image: none;
    }
    .ckwriterBg .ckwriterPosts {
        background-color: var(--black3);
    }
    .ckwriterPost .postTitle {
        color: var(--silver2);
    }
    .ckwriterPost .postInfo-job {
        color: var(--silver3);
    }
    .ckwriterPost .postTitle,
    .ckwriterPost .postThumb {
        border: 0px;
    }
    .ckwriterPost .postInfo::after {
        display: none;
    }
    .pickedTopics .pickedTopic .pickedTopic-head,
    .editorRecommend .editorRecommend-head {
        background-image: none;
        background-color: var(--blue1);
    }
    .editorRecommend.noBgblur h4 a {
        color: var(--silver3);
    }
    .writerContainer {
        background-image: none;
    }
    .writerContainer .writerWrapper {
        background-color: var(--black3);
    }
    .writerContainer .writerWrapper .writer span,
    .titleBox .lookMore {
        color: var(--silver2)
    }
    .writerContainer .writerWrapperArrow.writerWrapperArrow__left,
    .ckStarWrappeArrow__left {
        background-image: url("https://ck101.com/static/image/index/writersArrowLeft_ylw.png");
        -webkit-filter: grayscale(10%) invert(80%) drop-shadow(1px 1px 1px #555555);
        filter: grayscale(10%) invert(80%) drop-shadow(1px 1px 1px #555555);
    }
    .writerContainer .writerWrapperArrow.writerWrapperArrow__right,
    .ckStarWrappeArrow__right {
        background-image: url("https://ck101.com/static/image/index/writersArrowRight_ylw.png");
        -webkit-filter: grayscale(10%) invert(80%) drop-shadow(1px 1px 1px #555555);
        filter: grayscale(10%) invert(80%) drop-shadow(1px 1px 1px #555555);
    }
    .sitemapContainer,
    .sitemapContainer .sitemapBox {
        background-image: none;
        background-color: var(--black3);
    }
    .sitemapContainer .sitemapBox .catName a {
        color: var(--silver4);
    }
    .sitemapContainer .sitemapBox .forums .forumName a {
        color: var(--silver2);
    }
    .rankBoardWrapper .rankList a:first-child {
        color: var(--silver1);
    }
    .ckStarWrapper__wide-bg,
    .ckStarWrapper__wide-box-bg {
        background-image: none;
        background-color: var(--black2);
    }
    /* thread page */
    .a_t span {
        background-color: var(--black3);
        color: var(--silver3) !important;
    }
    .viewthread {
        background-color: var(--black3);
    }
    #pt .z em {
        filter: invert(86%);
    }
    div#postlist.viewthread.pl.cl div#pt.cl div.y a img.vm {
        filter: invert(86%);
    }
    .pg a,
    .pgb a,
    .pg label,
    .pg {
        background-color: var(--black4);
        color: var(--silver3);
        border: none;
    }
    .pgb a,
    .pg a.nxt {
        background-image: none;
        padding: 0px 5px 0px 5px;
    }
    .pg a:hover,
    .pgb a:hover {
        border: 1px solid;
        border-color: var(--blue1);
        color: var(--silver3)
    }
    .pg a.prev {
        background-color: #fff;
        filter: invert(77%);
    }
    .frameBase {
        background-color: var(--black3);
    }
    .floorTools {
        background-color: var(--black4);
        border: 1px solid var(--black2);
    }
    .plsTitle {
        background-color: var(--black2);
        height: 86px;
    }
    .authorDetails {
        padding-left: 15px;
    }
    [class="toptoolbar pos_relative"] ul {
        padding-right: 15px;
    }
    .btn_addbookshelf_t30 {
        right: 15px;
    }
    /* list page */
    .forumForm tr:hover,
    .fl_box tr:hover {
        background-color: var(--black4);
    }
    .sortOrder {
        background-color: var(--black3);
    }
    .filterBox .showmenu,
    .p_pof,
    .p_pop,
    .sllt {
        background-color: var(--black4);
    }
    .ttp .sort {
        color: var(--silver4);
    }
    .blockTitle a {
        color: var(--silver3);
    }
}
