css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("client.canternet.org") {

/* I guess I'll try to document what I'm doing here to make this easier to get started. */

/* This covers the general page color background and text. */
.dynamicpanel, .tabbar, #ircui {
    background-color: #111 !important;
    color: #dedede !important;
    font-family: sans-serif !important;
    border-bottom: 2px solid !important;
}

.topic {
    background-color: #111 !important;
}

/* This covers the tab bar at the top of the chat area. */
.tabbar > a {
    background-color: #fff !important;
}

/* These are for the back and forth line patterns (zebra striping) for each thing said here. */
.linestyle1 {
    background-color: #121212 !important;
    color: #dedede !important;
}
.linestyle2 {
    color: #dedede !important;
}

/* This covers the links posted in chat and the topic lines. */
.ircwindow a, .topic a {
    color: #face50 !important;
}

/* This handles highlights of the nickname */
.Xc4 {
    color: #feca10 !important;
    text-shadow: 0 0 1px #aceace !important;
}

/* This is for the nickname list on the right, but does NOT change the underline. */
.nicklist {
    background-color: #111 !important;
    border: 0 !important;
    border-left: 3px solid !important;
}

/* This is for the underline that is in the nickname list on the right of the display. */
.nicklist > a {
    border: 0 !important;
    border-bottom: 1px solid #223 !important;
    font: 10pt monospace !important;
    color: #face50 !important;
}

/* This is for the input area, so it stops being that bastion of white in a charcoal sea. */
.keyboard-input {
    background-color: #191919 !important;
    color: #eeeeee !important;
    font: 10pt monospace !important;
}

}
