css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')  {
body {
    background: url(http://www.pictureshack.us/images/97077_untitled-1.png') repeat fixed !important;
  }
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: url('http://static.tumblr.com/6oysso7/Ykmmfpd0k/cont.png');
padding: 20px;
-webkit-border-radius: 15px;
-moz-border-radius: 15px;
border-radius: 15px;
}

div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #000;
opacity:0.6;
font-size: 0;
position: relative;
top: 4px;
vertical-align: top;
margin: 0 auto;
display: none;
}

#logo {height: 0 !important;width: 0 !important;padding-left: 230px !important;padding-top: 80px !important; background: url(http://25.media.tumblr.com/5f1485c3596ec4c00571eb9352952bde/tumblr_mpwinxSDHn1s1k14zo1_500.png) no-repeat !important;}


#right_column .controls_section .recessed {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none;
-webkit-border-bottom-right-radius: 6px;
-webkit-border-bottom-left-radius: 6px;
-moz-border-radius-bottomright: 6px;
-moz-border-radius-bottomleft: 6px;
border-bottom-right-radius: 6px;
border-bottom-left-radius: 6px;
position: relative;

}

#right_column .controls_section .recessed:hover {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: 0 0 5px #000;
-moz-box-shadow: 0 0 5px #000;
box-shadow: 0 0 5px #000;
}

#right_column .controls_section .selected {
background: #fcb3c6;
background: -moz-linear-gradient(top,#fcb3c68 0,#fcb3c6 50%,#fcb3c6 50%,#fcb3c6 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#fcb3c6),color-stop(50%,#fcb3c6),color-stop(50%,#fcb3c6),color-stop(100%,#fcb3c6));
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#fcb3c6',endColorstr='#fcb3c6',GradientType=0);
-webkit-box-shadow: 0 0 5px #fcb3c6,inset 0 1px 0 rgba(255,255,255,.40);
-moz-box-shadow: 0 0 5px #fcb3c6,inset 0 1px 0 rgba(255,255,255,.40);
box-shadow: 0 0 5px #fcb3c6,inset 0 1px 0 rgba(255,255,255,.40);
-webkit-border-radius: 1px;
-moz-border-radius: 1px;
border-radius: 1px;
border-top: none;
}

#right_column .controls_section .selected:hover {
background: #fcb3c6;
background: -moz-linear-gradient(top,#fcb3c6 0,#fcb3c6 50%,#fcb3c6 50%,#fcb3c6 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#fcb3c6),color-stop(50%,#fcb3c6),color-stop(50%,#fcb3c6),color-stop(100%,#fcb3c6));
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#c31c16',endColorstr='#6B903E',GradientType=0);
-webkit-box-shadow: 0 0 5px #fcb3c6;
-moz-box-shadow: 0 0 5px #fcb3c6;
box-shadow: 0 0 5px #fcb3c6;

border-top: none;
}

#right_column .radar .content {
background: url('http://static.tumblr.com/6oysso7/F0Tmfnjlu/bg.png') repeat-y;
position: relative;
}

#right_column .radar .content.is_media .mask.top {
height: 9px;
background: none;
background-position: 0 0;
top: 0;
}

#right_column .radar .content.is_media .mask.bottom {
height: 19px;
background: none;
background-position: 0 -9px;
bottom: 0;
}

img#content_top, img#content_bottom {
display:none;
}}
