/* AGENT_SHEET */
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/*  Designed by Noah Granger on July 13, 2013 == http://ngranger.x10.mx  */
/* == Vertical Scrollbar == */
scrollbar[orient="vertical"] {
  cursor: default; -moz-appearance: none; background: #F2F2F2; border-left: 1px solid #ADADAD;}
  scrollbar[orient="vertical"] slider {
    background: transparent; padding: 1px !important;}
  scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
    background: #D9D9D9; min-height: 17px; height: 17px;  max-height: 17px; -moz-border-bottom-colors: #ADADAD !important;
    -moz-appearance: none; border-width: 0 0 1px 0; border-style: solid; border-color: #ADADAD;}
    scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"]:hover {background-color: #C2C2C2; -moz-border-bottom-colors: #9A9A9A;}
    scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"]:active {background-color: #919191; -moz-border-bottom-colors: #737373;}
  scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] {
    background: #D9D9D9; min-height: 17px;  height: 17px;  max-height: 17px; -moz-border-top-colors: #ADADAD !important;
    -moz-appearance: none; border-width: 1px 0 0 0; border-style: solid; border-color: #ADADAD;}
    scrollbar[orient="vertical"] > scrollbarbutton[type="increment"]:hover {background-color: #C2C2C2; -moz-border-top-colors: #9A9A9A;}
    scrollbar[orient="vertical"] > scrollbarbutton[type="increment"]:active {background-color: #919191; -moz-border-top-colors: #737373;}
  thumb[orient="vertical"] {
    -moz-appearance: none; background: #D9D9D9; border: 1px solid #ADADAD; border-radius: 1px;}
    thumb[orient="vertical"]:hover {background-color: #C2C2C2; border-color: #9A9A9A;}
    thumb[orient="vertical"]:active {background-color: #919191; border-color: #737373;}
/* == Horizontal Scrollbar == */
scrollbar[orient="horizontal"] {
  cursor: default; -moz-appearance: none; background: #F2F2F2; border-top: 1px solid #ADADAD;}
  scrollbar[orient="horizontal"] slider {
    background: transparent; padding: 1px !important;}
  scrollbar[orient="horizontal"] > scrollbarbutton[type="decrement"] {
    background: #D9D9D9; min-height: 17px; height: 17px;  max-height: 17px;
    -moz-appearance: none; border-width: 0 1px 0 0; border-style: solid; border-color: #ADADAD;}
    scrollbar[orient="horizontal"] > scrollbarbutton[type="decrement"]:hover {background-color: #C2C2C2; border-color: #9A9A9A;}
    scrollbar[orient="horizontal"] > scrollbarbutton[type="decrement"]:active {background-color: #919191; border-color: #737373;}
  scrollbar[orient="horizontal"] > scrollbarbutton[type="increment"] {
    background: #D9D9D9; min-height: 17px;  height: 17px;  max-height: 17px;
    -moz-appearance: none; border-width: 0 0 0 1px; border-style: solid; border-color: #ADADAD;}
    scrollbar[orient="horizontal"] > scrollbarbutton[type="increment"]:hover {background-color: #C2C2C2; border-color: #9A9A9A;}
    scrollbar[orient="horizontal"] > scrollbarbutton[type="increment"]:active {background-color: #919191; border-color: #737373;}
  thumb[orient="horizontal"] {
    -moz-appearance: none; background: #D9D9D9; border: 1px solid #ADADAD; border-radius: 1px;}
    thumb[orient="horizontal"]:hover {background-color: #C2C2C2; border-color: #9A9A9A;}
    thumb[orient="horizontal"]:active {background-color: #919191; border-color: #737373;}
