css:
@-moz-document domain(dailymotion.com) {



/* --- Page 1 --- */

.top_left, .top_right, .header_main_menu,

.subheader /* pub */, .centerbanner /* pub */, .featured_box, .dmpi_box_masscasttop /* pub */, .dmpi_slotlist /* video lists */, .dm_widget_masscast /* pub */, .tag_box,

/* --- Boxes ---- */

.box_header, .dmpi_slotitem, .box_footer,

/* --- Page 2 --- */

#top_banner,
#right_content_box,
#comment_list,

#footer,

/* --- Page 3 --- */

/* --- Top ------ */

.header_top, .main_menu, .right_menu,

.left_boxes, .right_boxes,

#dm_footer

{display: none !important;}



/* --- Fixes --- */

body, .content_right, .box_content, .box_content_right { background: #000 none !important; }

/* --- Buttons - */

input, select, .button {
 background: none #000 !important;
 border-color: #333 !important;
 color: #bbb !important; 
 -moz-border-radius: 3px !important;
}

.box_content, .content_right { text-align: center !important; }
.dmpi_video_item { height: 160px !important; }
.dmco_image { border: solid 1px #333 !important; }

}
