@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain(naxoseduccion.blogspot.com) 

{



/* Color de Fondo*/

 .content-inner { background-color:#080808 !important; }

 

 /*Titulo 1*/

 .post-title.entry-title { text-align:center;}

 

 /* Fecha y subtitulos */

 .date-header, span, .comment-author, h4 {color:#f0f0f0 !important;}

 

 /*Colores de Links */

 a:link {color:#f0f0f0 !important;}

 a:visited {color:#606060 !important;}

 a:hover {color:#ffffff !important;}

 a:active {color:#ffffff !important;}

 h3.post-title.entry-title {color:#f0f0f0 !important;}

 

 /* Texto del articulo   */

 .post-body.entry-content, .MsoNormal {color:#a8a8a8 !important;}

 blockquote {color:#a8a8a8 !important;}

 

 

 /* Parrafos con negrita */

 strong, p {color:#a8a8a8 !important;}

 

 #PageList1.widget.PageList { display:none }

 #crosscol.tabs.section { display:none }

 

/* Color de botones al final de pag  */

 #blog-pager.blog-pager { background-color:transparent !important; }

 a#Blog1_blog-pager-older-link.blog-pager-older-link, 

 a#Blog1_blog-pager-newer-link.blog-pager-newer-link, 

 a.home-link { background-color: #000000 !important; }

 

/*Colummna Lado derecho*/

 .item-snippet {color:#a8a8a8 !important;}

 #BlogArchive1.widget.BlogArchive, h2 {color:#a8a8a8 !important;}

 dd.profile-textblock {color:#a8a8a8 !important;}



