css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {


.comments_ad_image, .item_ad_image, .top_ad_image, .hottest_ad_image {
display:none !important;
}



body {background:#4C5844 url(http://storefront.steampowered.com/v4/img/background.gif) !important;
color:#CFCBC2 !important;
background-position:center top !important;
background-repeat:repeat !important;
}

#contents , #h li a, #h-pri div a, .comment li ul{
background-color:#2A2A2A !important;
color:#6EBCFF !important;
}

#h-pri li a strong, #h-pri div a strong{
background-color:#2A2A2A !important;
color:#6EBCFF !important;
}

.footer-contents, #footer {
background: #222222 !important;
}

.copyright, .copyright div {
background: #000000 !important;
}

join-digg p, #announce p, #sub-nav li.active, .crumb div {
background:#2A2A2A!important;
border:1px solid #8BC53F!important;
color:#8BC53F!important;
font-weight:bold!important;
-moz-border-radius:3px !important
}

#join-digg a, #announce a, .selector h2, .selector .tool, .selector .tool:visited, #sub-nav li, .crumb  {
background:#2A2A2A!important;
color:#8BC53F!important;
border:none !important;
}

.comment li.c-overflow, .comment form textarea {
background: #1a1a1a !important;
border-color:#8BC53F!important;
color:#8BC53F !important;}

.c-diggs a, .c-diggs a em, .c-diggs em, h3 a, .comment a {
color:#8BC53F !important;
}

#h  {
background: #000 !important;
}

#h-pri  {
background: #222 !important;
}

#h-sec  {
background: #2a2a2a !important;
}

.submenu-drop {
padding:1px !important;
border-color:#8BC53F !important;
}

.tab h3, .profile-nav, .profile-nav li.active a{
background:#1a1a1a !important;
color:#8BC53F !important;
-moz-border-radius:3px !important;
border:1px solid #8BC53F!important;
padding:3px !important;
}

.tab, .profile-form div.tab {
background:transparent !important;
}

.tab-details, .confirm, .error, .warning, .info, .positive, .notice, .vcard, .profile-nav li a, .submission .topics-container ul li a {
background:#2A2A2A!important;
color:#6EBCFF !important;
border:1px solid #6EBCFF!important;
}

li.c-disabled, li.c-disabled-open{
background:#000!important;
-moz-opacity: 0.3 !important;
}

#wrapper {
padding-top:0 !important;
}

.profile-nav li {
padding:0 0 0 0 !important;
}

.submission .topics-container{
background:#2a2a2a !important;
color: #6EBCFF !important;
}

.submission .topics-container h4 {
color:#8BC53F !important;

}

.submission .topics-container:hover {
background:#1a1a1a !important;
}

.submission .topics-container ul li a:hover {
background:#1a1a1a!important;
color:#8BC53F !important;
border:1px solid #8BC53F!important;
}

.form-large .form-text, .form-large .form-text-variable, .form-large textarea, #topic-default1, #topic-default2, #topic-default3, .form-large select, .form-large .form-brief{background:#000 !important; border-color:#fff !important; border-bottom-width:1px !important; color:#666 !important; }


.form-large .form-text:hover, .form-large .form-text-variable:hover, .form-large textarea:hover, #topic-default1:hover, #topic-default2:hover, #topic-default3:hover, .form-large select:hover, .form-large .form-brief:hover{background:#000 !important; border-color:#33CCFF !important; color:#6EBCFF !important;}


}
