css:
@-moz-document url-prefix("http://www.bilibili.com/video"),url-prefix("http://bangumi.bilibili.com/anime/v"){
    .header-layer,
    .header-link,
.h-center,
.arc-toolbar,
    .footer,
    .b-page-small,
    .player-wrapper + .main-inner,
    [class^="ad-"],
    .v-title-line,
    .upinfo,
.b-header-mask-wrp,
.z_header,
.random-p-movie,
#mobile_p,
.charge-appeal-init,
.tag-list-wrp,
#bpoints_win,
.bangumi-buybuybuy,
.v1-bangumi-info-wrapper,
.sponsor-content-wrapper,
.v_small,
.v_large,
.topview-sponsor,
.v-title-info
{
  display:none !important;
}


    
    .large, .b-page-large {
    width: 960px;
}
    
    .vd-list.l3 li {
    float: left;
    padding-top: 4px;
    padding-right: 4px;
}
    
    .vd-list.l3 li .l-item {
    border: 1px solid #E5E9EF;
    overflow: hidden;
    height: 208px;
    width: 186px;
    border-radius: 4px;
}
    
    
    .vd-list.l3 li .preview {
    margin: 2px 0px 2px 2px;
    float: none;
}
.vd-list li .preview {
    width: 181px;
    height: 113px;
    overflow: hidden;
    display: block;
    text-align: center;
    float: left;
    margin: 0px 20px 0px 0px;
    background: #333 none repeat scroll 0% 0%;
    border-radius: 4px;
}
.vd-list li .preview img {
    margin: 0px auto;
    height: 114px;
    max-width: 181px;
    display: block;
}
    
    .viewbox {
    padding: 0px;
}
    
    .viewbox .info {
    float: left;
    width: 640px;
    position: relative;
    overflow: hidden;
    padding: 0px;
}
    .player-wrapper {
    padding: 4px 0px;
}
    
    #plist{
        height: 25px;
    }
    
    #bofqi {
    margin: 0px auto 0px;
    }

.header {
    z-index: 10000000;
}
}

@-moz-document url-prefix("http://www.bilibili.com/search"){
[id^="ads"],
  .comprehensive-right,
  .footer
{
  display:none !important;
}
}
