css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.last.fm/user/), url-prefix(http://www.last.com.br/user/),url-prefix(http://www.lastfm.de/user/),url-prefix(http://www.lastfm.es/user/),url-prefix(http://www.lastfm.fr/user/), url-prefix(http://www.lastfm.it/user/),url-prefix(http://www.lastfm.pl/user/),url-prefix(http://www.lastfm.se/user/),url-prefix(http://www.lastfm.com.tr/user/),url-prefix(http://www.lastfm.ru/user/),url-prefix(http://www.lastfm.jp/user/),url-prefix(http://cn.last.fm/user/) {

#page {
	font-family: "Trebuchet MS", "Lucida Grande", Arial, Helvetica, Verdana, sans-serif !important;	
        max-width: 100% !important;
        background-color: #BDBEC6 !important;   
}

#taste {
    height: 0px !important;
    overflow: hidden !important;
}

#secondaryNavigation {
    width:250px !important;
    position: absolute !important;
    left: -266px !important;
    top: -1px !important;
    z-index: 10 !important;
    font-size: 11px !important;
    line-height: 2em !important;
    font-weight: normal !important;
    overflow: hidden !important;       
    padding-left: 8px !important;
    padding-right: 8px !important;
    background-color: #BDBEC6 !important;   
}

#secondaryNavigation div {
    width: 214px !important;
    background-color: #FFFFFF !important;
    margin: 0px 0px 16px 10px !important;
    padding: 0px 8px 0px 8px !important;    
}

#secondaryNavigation div div {
    width: auto !important;
    background-color: #FFFFFF !important;
    margin: 0px 0px 0px 0px !important;    
    padding: 4px !important;
    
}

#secondaryNavigation a, #secondaryNavigation img {
    background: none !important;
    border: none !important;
    padding: 0px !important;
    display:-moz-inline-box !important;
    margin:0px !important;
}

#secondaryNavigation .profileImage
{
  padding: 0px !important;
  margin: 0px !important;
  clear:both !important; 
}

#secondaryNavigation .profileImage img
{
  height:75% !important;
  width: 75% !important;
}


div.profileHead div.profileImage {
    display: block !important;
    float: none !important;
    display: inline !important;
    margin: 0px auto 0px auto !important;
    width: 130px !important;
    text-align: center !important;
}

div.profileHead div.profileImage span.userImage {
    display: block !important;
    float: none !important;
    margin: 0px 0px 0px 0px !important;
}

div.profileHead div.userData
{
    margin: 0 0 0 0px !important;
    text-align: center !important;
}


#secondaryNavigation strong {
    padding-left:10px !important;
    margin: 0px !important;
}

#secondaryNavigation small {
    margin-top: -12px !important;
}

#secondaryNavigation ul {
    border:none !important;
}

#secondaryNavigation li {
    border:none !important;
}

#secondaryNavigation H2 {
    padding-left:0px !important;   
}

#groups {
    
}

#groups ul {
    margin-left: -50px !important;     
}


#groups a {
    display:inline !important;
}


#content {
    margin-left:250px !important;
    margin-top: 16px !important;
    margin-right: -8px !important;
    border: none !important;  
    background: none !important; 
}

#content div {
   background-image: none !important;
}


#navmenu {
    margin-left: -15px !important;
    margin-bottom: 32px !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
    background: url(http://cdn.last.fm/flatness/headings/h2_left_col.png) right top no-repeat #575757 !important;   
}

#navmenu li {
    padding-left: 6px !important;
    padding-right: 6px !important;
    max-height: 12px !important;
}

#navmenu a {
    color: #FFFFFF !important;
    font-weight: bold !important;
}

#LastFooter {
    margin-left:249px !important;
    margin-right: 327px !important;
}  

#primaryNav {
    margin-left: 136px !important;
}

#profilehead {
    margin-top: 12px !important;
}

#secondaryNavigation .h2Brushed {
    border-bottom: none !important;    
}

.leftCol
{
    background-color: #BDBEC6 !important;
    margin-top: -1px !important; 
}

.leftColWrapper 
{
    margin-right: 336px !important;
    background-color: #ffffff !important;
    padding-top: 0px !important;
}

.rightCol
{
    background: none !important;
    margin-top: -16px !important;
    
}
 

.rightCol div.module
{
    background: #ffffff !important;
    margin: 0px 0px 32px 0px !important;     
}

#shoutbox
{
    padding: 0px 12px 36px 8px !important; 
}


.rightCol H2
{
    border:none !important;   
}

.rightCol
{
    background: none !important;
}

#page div.fiflufi div.rightCol h2,
#page div.fixed div.rightCol h2 {
    margin: 0px 0px 12px -12px !important;
}


#page div.fiflufi div.rightCol h2 .h2Wrapper,
#page div.fixed div.rightCol h2 .h2Wrapper {
    background: url(http://cdn.last.fm/flatness/headings/h2_left_col.png) right top no-repeat #575757 !important;
}

.pagehead, .skylineRight
{
   background: #ffffff !important;  
}

.xerLeftCol
{
   width:232px !important;
   margin-right: 0px !important;
   position: absolute !important;       
}


.sic #content
{
   background: #ffffff !important;
}


}
