css:
@-moz-document domain("ech.su") {
/*
 * Новый стиль для ech.su
 */

/* Теги */

html {
    background-color: #F3F3F3 !important;
}

body {
    background-color: #F3F3F3 !important;
    color: red !important;
    font-family: "Trebuchet MS",Trebuchet,serif !important;
    margin: 0 !important;
    padding-top: 15px !important;
    width: 95% !important;
}

table {
	border-style: none !important;
}

table td {
	border-style: none !important;
}

blockquote {
    color: #F3F3F3 !important;
    text-shadow: #B00C00 1px 0px 0.3em, #B00C00 1px 1px 0.3em, #B00C00 0px 1px 0.3em, #B00C00 -1px 1px 0.3em, #B00C00 -1px 0px 0.3em, #B00C00 -1px -1px 0.3em, #B00C00 0px -1px 0.3em, #B00C00 1px -1px 0.3em !important;
}

blockquote p {
	margin: 0 !important;
	padding: 0 !important;
}

a {
    color: #FF0000 !important;
    text-shadow: white 0px 0px !important;
}

a:hover {
    color: #BE0000 !important;
}

blockquote a {
	color: #D6D6D6 !important;
	text-shadow: #FF2727 1px 0px 0.3em, #FF2727 1px 1px 0.3em, #FF2727 0px 1px 0.3em, #FF2727 -1px 1px 0.3em, #FF2727 -1px 0px 0.3em, #FF2727 -1px -1px 0.3em, #FF2727 0px -1px 0.3em, #FF2727 1px -1px 0.3em !important;
}

blockquote a:hover {
	color: #EAD3D3 !important;
}

hr {
    border-color: red !important;
}

/* Классы */

.menu {
    margin-top: 1em !important;
    text-align: center !important;
}

.content {
    margin-left: 2.5em !important;
    text-align: justify !important;
    padding-left: 4px !important;
    background-color: #FFFFFF !important;
    border: 1px solid red !important;
}

.replymode {
    color: red !important;
    background-color: #FFFFFF !important;
    border: red 1px solid !important;
    border-radius: 0px !important;
    border-bottom-left-radius: 10em 5em !important;
    border-top-right-radius: 10em 5em !important;
}

.reply {
    background-color: #E4E1E1 !important;
    border-radius: 0px !important;
    border-bottom-left-radius: 10px !important;
    border-top-right-radius: 10px !important;
    border: red 2px inset !important;
    box-shadow: 0px 0px 4px red !important;
}

.reply blockquote {
	margin: .5em !important;
}

.unkfunc {
    color: white !important;
    text-shadow: green 1px 0px 0.3em, green 1px 1px 0.3em, green 0px 1px 0.3em, green -1px 1px 0.3em, green -1px 0px 0.3em, green -1px -1px 0.3em, green 0px -1px 0.3em, green 1px -1px 0.3em !important;
}

.newssub {
    position: absolute !important;
}

.header_img {
    background-color: white !important;
    border-bottom: 1px red solid !important;
    border-left: 1px red solid !important;
    border-right: 1px red solid !important;
}

.adminbar {
    background-color: white !important;
    border-bottom: 1px red solid !important;
    border-left: 1px red solid !important;
}

.adminbar a {
	font-weight: bold !important;
}

.postername {
    color: #BE0000 !important;
}

.spoiler {
    background-color: #FD5959 !important;
    text-shadow: #FD5959 1px 0px !important;
    box-shadow: 0px 2px 4px red, 0px -2px 4px red !important;
    color: #FD5959 !important;
}

.spoiler:hover {
    text-shadow: #B00C00 1px 0px 0.3em, #B00C00 1px 1px 0.3em, #B00C00 0px 1px 0.3em, #B00C00 -1px 1px 0.3em, #B00C00 -1px 0px 0.3em, #B00C00 -1px -1px 0.3em, #B00C00 0px -1px 0.3em, #B00C00 1px -1px 0.3em !important;
    color: #F3F3F3 !important;
}

.logo {
	clear:both !important;
	text-align:left !important;
	font-size:2em !important;
	font-weight: bold !important;
}

.filesize {
	text-decoration:none !important;
	color: #BF1B1B !important;
	font-size: .8em !important;
}

.omittedposts {
	color: #BF1B1B !important;
}

.postblock {
	background:transparent !important;
	font-size: .8em !important;
	color: #BF1B1B !important;
	font-weight:bold !important;
}

.reflink {
	font-size: .8em !important;
	font-weight: bold !important;
}

.doubledash { /* Кажись, его уже выпилили */
	display: none !important;
	vertical-align:top !important;
	clear:both !important;
	float:left !important;
}

.abbrev { /* Слово "Ответы:" у поста */
	color: #7E3737 !important;
	text-shadow: white 0px 0px !important;
}

/* Совместно классы и теги */

h2, h3, .logo {
    color: white !important;
    text-shadow: red 1px 0px 2px, red 1px 1px 2px, red 0px 1px 2px, red -1px 1px 2px, red -1px 0px 2px, red -1px -1px 2px, red 0px -1px 2px, red 1px -1px 2px !important;
}

h2, .top {
    border-color: #FF8484 !important;
    background-color: #FFF6F6 !important;
}

/* Пока не нашёл, где используются */
.theader, .passvalid{
	background:#DDDDDD !important;
	text-align:center !important;
	padding:2px !important;
	color:#2266AA !important;
	clear: both !important;
	font-weight: bold !important;
	margin-bottom: .5em !important;
	border: solid 1px #CCCCCC !important;
	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
}

.rules {
	font-size:0.6em !important;
}

.reflinkpreview {
	background:#DDDDDD !important;
	margin-bottom: .5em !important;
	border: solid 1px #CCCCCC !important;
	padding:0px !important;
	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
}

.footer {
	text-align:center !important;
	font-size:12px !important;
	font-family:serif !important;
	margin: 2em 0 0 0 !important;
}

.dellist {
	font-weight: bold !important;
	text-align:center !important;
}

.DESU_refmap {
margin:10px 4px 4px 4px !important;
font-size:70% !important;
font-style:italic !important;
}

.delbuttons {
	text-align:center !important;
	padding-bottom:4px !important;
}

.managehead {
	background:#DDDDDD !important;
	color:#002244 !important;
	padding:0px !important;
}

.postlists {
	background:#FFFFFF !important;
	width:100% !important;
	padding:0px !important;
	color:#800000 !important;
}

.row1 {
	background:#DDDDDD !important;
	color:#002244 !important;
}

.row2 {
	background:#CCCCCC !important;
	color:#002244 !important;
}

.filetitle {
	background:inherit !important;
	font-size:1.2em !important;
	color:#002244 !important;
	font-weight:bold !important;
}

.postertrip {
	color:#FF3300 !important;
}

.oldpost {
	color:#CC1105 !important;
	font-weight:bold !important;
}

.replytitle {
	font-size: 1.2em !important;
	color:#002244 !important;
	font-weight:bold !important;
}

.commentpostername {
	color:#004A99 !important;
	font-weight:800 !important;
}

.thumbnailmsg {
	font-size: .8em !important;
	color:#666666 !important;
}

.nothumb {
	background-color: #FFFFFF !important;
	border-style: dotted !important;
	margin: .3em .5em !important;
}

.highlight {
	background:#FFFFFF !important;
	border: 2px dashed #EE6600 !important;
}

p.spoiler > span.warning {
	color: #FF6600 !important;
	font-weight: bolder !important;
}
}
