css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bitbucket.org")
{
    body, div#wrapper, div#repo-content, tr, td, div.aui-sidebar-body, div.aui-page-panel, section#jira-issue-box
    {
        background-color: #d3d3d3;
    }
    
    header#repo-header, header#dashboard-header, div.toolbar, .linenos, div.heading, a.line-numbers, div.line-numbers-skipped
    {
        background-color: #c0c0c0 !important;
    }
    
    nav.aui-navgroup, footer, div#footer-logo
    {
        background-color: #b8b8b8 !important;
    }
    
    header#source-path, div.file, table#source-list td, article.news-item, article.readme, article.repository, section#repo-stats
    {
        background-color: #cacaca !important;
    }
    
    tt.docutils.literal, div#repo-info, div.issue-attrs
    {
        background-color: #e3e3e3 !important;
    }
    
    article.repository, article.news-item
    {
        padding-top: 5px;
        padding-bottom: 5px;
    }
    
    div.newsfeed div.aui-avatar
    {
        margin-left: -30px !important;
    }
    
    ol.clearfix
    {
        background-color: #d3d3d3 !important;
        background-image: none !important;
    }
    
    article.readme, div.readme
    {
        padding: 10px;
    }
    
    div.sidebar { width: 400px !important; }
}
